export { default as RoutingAPI } from './RoutingAPI';
export { default as StopsAPI } from './StopsAPI';
export { default as RealtimeAPI, RealtimeModes } from './RealtimeAPI';

import BaseObject from 'ol/Object';
import getUrlWithParams from '../utils/getUrlWithParams';

export type HttpAPIOptions = {
  url: string;
  apiKey?: string;
};
/**
 * Common class to access to a geOps api using http.
 *
 * @example
 * import { API } from 'mobility-toolbox-js/api';
 *
 * const api = new HttpApi({
 *   url: [yourUrl],
 *   apiKey: [yourApiKey]
 * });
 *
 * @classproperty {string} url Url of the service.
 * @classproperty {string} apiKey Api key to access the service.
 */
class HttpAPI extends BaseObject {
  url: string;

  apiKey?: string;

  constructor(options: HttpAPIOptions) {
    super();
    /** @ignore */
    this.url = options.url;

    /** @ignore */
    this.apiKey = options.apiKey;
  }

  /**
   * Append the apiKey before sending the request.
   * @ignore
   */
  fetch(path: string, params: Object, config: RequestInit): Promise<any> {
    if (!this.url) {
      // eslint-disable-next-line no-console
      return Promise.reject(
        new Error(`No url defined for request to ${this.url}/${path}`),
      );
    }
    if (!this.url && !this.apiKey && !/key=/.test(this.url)) {
      // eslint-disable-next-line no-console
      return Promise.reject(
        new Error(`No apiKey defined for request to ${this.url}`),
      );
    }

    // Clean requets parameters, removing undefined and null values.
    const searchParams = params || {};
    const url = getUrlWithParams(`${this.url}${path || ''}`, {
      key: this.apiKey,
      ...searchParams,
    });

    // We use toString  because of TYpeScript bug that only accept a string in fetch method.
    return fetch(url.toString(), config).then((response) => {
      try {
        return response.json().then((data) => {
          if (data.error) {
            throw new Error(data.error);
          }
          return data;
        });
      } catch (err: any | Error) {
        return Promise.reject(new Error(err));
      }
    });
  }
}

export default HttpAPI;

/**
 * Class used to facilitate connection to a WebSocket and
 * also to manage properly messages send to the WebSocket.
 * This class must not contain any specific implementation.
 */
class WebSocketAPI {
  constructor() {
    this.defineProperties();
  }

  defineProperties() {
    Object.defineProperties(this, {
      closed: {
        get: () =>
          !!(
            this.websocket &&
            this.websocket.readyState === this.websocket.CLOSED
          ),
      },
      closing: {
        get: () =>
          !!(
            this.websocket &&
            this.websocket.readyState === this.websocket.CLOSING
          ),
      },
      connecting: {
        get: () =>
          !!(
            this.websocket &&
            this.websocket.readyState === this.websocket.CONNECTING
          ),
      },
      open: {
        get: () =>
          !!(
            this.websocket && this.websocket.readyState === this.websocket.OPEN
          ),
      },
      /**
       * Array of message to send on open.
       * @type {Array<string>}
       * @private
       */
      messagesOnOpen: {
        value: [],
        writable: true,
      },
      /**
       * Array of subscriptions.
       * @type {Array<subscription>}
       * @private
       */
      subscriptions: {
        value: [],
        writable: true,
      },

      /**
       * List of channels subscribed.
       * @type {Array<subscription>}
       * @private
       */
      subscribed: {
        value: {},
        writable: true,
      },
    });
  }

  /**
   * Get the websocket request string.
   *
   * @param {string} method Request mehtod {GET, SUB}.
   * @param {Object} params Request parameters.
   * @param {string} params.channel Channel name
   * @param {string} [params.args] Request arguments
   * @param {Number} [params.id] Request identifier
   * @return {string} request string
   * @private
   */
  static getRequestString(method, params) {
    let reqStr = `${method} ${params.channel}`;
    reqStr += params.args ? ` ${params.args}` : '';
    reqStr += params.id ? ` ${params.id}` : '';
    return reqStr.trim();
  }

  /**
   * (Re)connect the websocket.
   *
   * @param {strin} url Websocket url.
   * @param {function} onOpen Callback called when the websocket connection is opened and before subscriptions of previous subscriptions.
   * @private
   */
  connect(url, onOpen = () => {}) {
    if (this.websocket && !this.closed) {
      this.websocket.close();
    }

    /** @ignore */
    this.websocket = new WebSocket(url);

    if (!this.open) {
      this.websocket.addEventListener('open', () => {
        onOpen();
        this.subscribePreviousSubscriptions();
      });
    } else {
      onOpen();
      this.subscribePreviousSubscriptions();
    }
  }

  /**
   * Close the websocket definitively.
   *
   * @private
   */
  close() {
    if (this.websocket) {
      this.websocket.onclose = null;
      this.websocket.close();
      this.websocket = null;
      this.messagesOnOpen = [];
    }
  }

  /**
   * Sends a message to the websocket.
   *
   * @param {message} message Message to send.
   * @private
   */
  send(message) {
    if (!this.websocket) {
      return;
    }
    const send = () => {
      this.websocket.send(message);
    };
    if (!this.open) {
      // This 'if' avoid sending 2 identical BBOX message on open,
      if (!this.messagesOnOpen.includes(message)) {
        this.messagesOnOpen.push(message);
        this.websocket.addEventListener('open', () => {
          this.messagesOnOpen = [];
          send();
        });
        this.websocket.addEventListener('close', () => {
          this.messagesOnOpen = [];
        });
      }
    } else if (!this.messagesOnOpen.includes(message)) {
      send();
    }
  }

  addEvents(onMessage, onError) {
    if (this.websocket) {
      this.websocket.addEventListener('message', onMessage);

      if (onError) {
        this.websocket.addEventListener('error', onError);
        this.websocket.addEventListener('close', onError);
      }
    }
  }

  removeEvents(onMessage, onError) {
    if (this.websocket) {
      this.websocket.removeEventListener('message', onMessage);

      if (onError) {
        this.websocket.removeEventListener('error', onError);
        this.websocket.removeEventListener('close', onError);
      }
    }
  }

  /**
   * Listen to websocket messages.
   *
   * @param {Object} params Parameters for the websocket get request
   * @param {function} cb callback on listen
   * @param {function} errorCb Callback on error
   * @return {{onMessage: function, errorCb: function}} Object with onMessage and error callbacks
   * @private
   */
  listen(params, cb, errorCb) {
    // Remove the previous identical callback
    this.unlisten(params, cb, errorCb);

    // We wrap the message callback to be sure we only propagate the message if it is for the right channel.
    const onMessage = (evt) => {
      let data = {};
      try {
        data = JSON.parse(evt.data);
      } catch (err) {
        // eslint-disable-next-line no-console
        console.error('WebSocket: unable to parse JSON data', err, evt.data);
      }
      let source = params.channel;
      source += params.args ? ` ${params.args}` : '';

      // Buffer channel message return a list of other channels to propagate to proper callbacks.
      const contents = data.source === 'buffer' ? data.content : [data];
      contents.forEach((content) => {
        // Because of backend optimization, the last content is null.
        if (
          content?.source === source &&
          (!params.id || params.id === data.client_reference)
        ) {
          cb(content);
        }
      });
    };

    this.addEvents(onMessage, errorCb);

    return { onMessageCb: onMessage, onErrorCb: errorCb };
  }

  /**
   * Unlisten websocket messages.
   *
   * @param {Object} params Parameters for the websocket get request.
   * @param {function} cb Callback used when listen.
   * @private
   */
  unlisten(params, cb) {
    [...(this.subscriptions || []), ...(this.requests || [])]
      .filter(
        (s) => s.params.channel === params.channel && (!cb || s.cb === cb),
      )
      .forEach(({ onMessageCb, onErrorCb }) => {
        this.removeEvents(onMessageCb, onErrorCb);
      });
  }

  /**
   * Sends a get request to the websocket.
   * The callback is called only once, when the response is received or when the call returns an error.
   *
   * @param {Object} params Parameters for the websocket get request
   * @param {function} onMessage callback on message event
   * @param {function} onError Callback on error and close event
   * @private
   */
  get(params, cb, errorCb) {
    const requestString = WebSocketAPI.getRequestString('GET', params);
    this.send(requestString);

    // We wrap the callbacks to make sure they are called only once.
    const once =
      (callback) =>
      (...args) => {
        callback(...args);
        const index = this.requests.findIndex(
          (request) =>
            requestString === request.requestString && cb === request.cb,
        );
        const { onMessageCb, onErrorCb } = this.requests[index];
        this.removeEvents(onMessageCb, onErrorCb);
        this.requests.splice(index, 1);
      };

    const { onMessageCb, onErrorCb } = this.listen(
      params,
      once(cb),
      once(errorCb),
    );

    // Store requests and callbacks to be able to remove them.
    if (!this.requests) {
      this.requests = [];
    }
    const index = this.requests.findIndex(
      (request) => requestString === request.requestString && cb === request.cb,
    );
    const newReq = {
      params,
      requestString,
      cb,
      errorCb,
      onMessageCb,
      onErrorCb,
    };
    if (index > -1) {
      this.requests[index] = newReq;
    } else {
      this.requests.push(newReq);
    }
  }

  /**
   * Subscribe to a given channel.
   *
   * @param {Object} params Parameters for the websocket get request
   * @param {function} cb callback on listen
   * @param {function} errorCb Callback on error
   * @param {boolean} quiet if false, no GET or SUB requests are send, only the callback is registered.
   * @private
   */
  subscribe(params, cb, errorCb, quiet = false) {
    const { onMessageCb, onErrorCb } = this.listen(params, cb, errorCb);
    const reqStr = WebSocketAPI.getRequestString('', params);

    const index = this.subscriptions.findIndex(
      (subcr) => params.channel === subcr.params.channel && cb === subcr.cb,
    );
    const newSubscr = { params, cb, errorCb, onMessageCb, onErrorCb, quiet };
    if (index > -1) {
      this.subscriptions[index] = newSubscr;
    } else {
      this.subscriptions.push(newSubscr);
    }

    if (!this.subscribed[reqStr]) {
      if (!newSubscr.quiet) {
        this.send(`GET ${reqStr}`);
        this.send(`SUB ${reqStr}`);
      }
      this.subscribed[reqStr] = true;
    }
  }

  /**
   * Unsubscribe from a channel.
   * @param {string} source source to unsubscribe from
   * @param {function} cb Callback function to unsubscribe. If null all subscriptions for the channel will be unsubscribed.
   * @private
   */
  unsubscribe(source, cb) {
    const toRemove = this.subscriptions.filter(
      (s) => s.params.channel === source && (!cb || s.cb === cb),
    );

    toRemove.forEach(({ onMessageCb, onErrorCb }) => {
      this.removeEvents(onMessageCb, onErrorCb);
    });

    this.subscriptions = this.subscriptions.filter(
      (s) => s.params.channel !== source || (cb && s.cb !== cb),
    );

    // If there is no more subscriptions to this channel, and the removed subscriptions didn't register quietly,
    // we DEL it.
    if (
      source &&
      this.subscribed[source] &&
      !this.subscriptions.find((s) => s.params.channel === source) &&
      toRemove.find((subscr) => !subscr.quiet)
    ) {
      this.send(`DEL ${source}`);
      this.subscribed[source] = false;
    }
  }

  /**
   * After an auto reconnection we need to re-subscribe to the channels.
   */
  subscribePreviousSubscriptions() {
    // Before to subscribe previous subscriptions we make sure they
    // are all defined as unsubscribed, because this code is asynchrone
    // and a subscription could have been added in between.
    Object.keys(this.subscribed).forEach((key) => {
      this.subscribed[key] = false;
    });

    // Subscribe all previous subscriptions.
    [...this.subscriptions].forEach((s) => {
      this.subscribe(s.params, s.cb, s.errorCb, s.quiet);
    });
  }
}

export default WebSocketAPI;

import BaseObject from 'ol/Object';

/**
 * A class representing a control to display on map.
 *
 * @example
 * const control = new Control();
 *
 * @classproperty {ol/Map~Map|mapboxgl.Map} map - The map which the control refers to.
 * @classproperty {boolean} active - Active the control.
 * @classproperty {HTMLElement} element - The HTML element used to render the control.
 * @classproperty {HTMLElement} target - The HTML element where to render the element property. Default is the map's element. Read only.
 */
class Control extends BaseObject {
  /**
   * Constructor
   *
   * @param {Object} [options] Control options.
   * @param {boolean} [options.active = true] Whether the control is active or not.
   * @param {HTMLElement} [options.element] The HTML element used to render the control.
   * @param {HTMLElement} [options.target] The HTML element where to render the element property. Default is the map's element.
   * @param {function} [options.render] Render function called whenever the control needs to be rerendered.
   */
  constructor(options = {}) {
    super(options);
    this.defineProperties(options);

    const { active } = {
      active: options.active !== false,
      ...options,
    };

    /**
     * @ignore
     */
    this.active = active;
  }

  /**
   * Define control's properties.
   *
   * @private
   */
  defineProperties(options) {
    const { target, element, render } = {
      ...options,
    };

    Object.defineProperties(this, {
      active: {
        get: () => this.get('active'),
        set: (newActive) => {
          this.set('active', newActive);
          if (newActive) {
            this.activate();
          } else {
            this.deactivate();
          }
          this.render();
        },
      },
      map: {
        get: () => this.get('map'),
        set: (map) => {
          // Remove previous node.
          if (this.map && this.element && this.element.parentNode) {
            this.element.parentNode.removeChild(this.element);
          }

          // Clean listeners
          this.deactivate();

          this.set('map', map);

          if (this.map) {
            // Add new node
            const targett =
              this.target ||
              (this.map.getTargetElement && this.map.getTargetElement()) ||
              (this.map.getContainer && this.map.getContainer());

            if (!this.element) {
              this.createDefaultElement();
            }

            if (this.element) {
              targett.appendChild(this.element);
            }

            // Add listeners
            if (this.active) {
              this.activate();
            }
          }
          this.render();
        },
      },
      target: {
        value: target,
      },
      element: {
        value: element,
        writable: true,
      },
      render: {
        /** @ignore */
        value: render || this.render,
        writable: true,
      },
    });
  }

  /**
   * Attach the control to the map. Add events, html element ...
   */
  attachToMap(map) {
    this.map = map;
  }

  /**
   * Detach the control From the map. Remove events, html element ..
   */
  detachFromMap() {
    this.map = null;
  }

  /**
   * Add listeners then renders the control.
   * To be defined in inherited classes.
   */
  activate() {
    this.deactivate();
  }

  /**
   * Remove listeners added by activate() function then renders the control.
   * To be defined in inherited classes.
   */
  // eslint-disable-next-line class-methods-use-this
  deactivate() {}

  /**
   * The default render function. It renders content in the HTML element.
   * To be defined in inherited classes.
   *
   * @private
   */
  render() {}

  /**
   * The default element to display if this.element is not defined.
   * To be defined in inherited classes.
   *
   * @private
   */
  // eslint-disable-next-line class-methods-use-this
  createDefaultElement() {}
}

export default Control;

import BaseObject from 'ol/Object';
import { v4 as uuid } from 'uuid';
import getLayersAsFlatArray from '../utils/getLayersAsFlatArray';

/**
 * A class representing a layer to display on map.
 *
 * @example
 * const layer = new Layer({
 *   name: 'My Layer',
 * });
 *
 * @classproperty {string} key - Identifier of the layer. Must be unique.
 * @classproperty {string} name - Name of the layer
 * @classproperty {string[]} copyrights - Array of copyrights.
 * @classproperty {Layer[]} children - List of children layers.
 * @classproperty {boolean} visible - Define if the layer is currently display on the map.
 * @classproperty {boolean} disabled - Define if the layer is currently display on the map but can't be seen (extent, zoom ,data restrictions).
 * @classproperty {number} hitTolerance - Hit-detection tolerance in css pixels. Pixels inside the radius around the given position will be checked for features.
 * @classproperty {Object} properties - Custom properties.
 * @classproperty {ol/Map~Map|mapboxgl.Map} map - The map where the layer is displayed.
 */
export default class Layer extends BaseObject {
  /**
   * Constructor
   *
   * @param {Object} options
   * @param {string} [options.key=uuid()] Identifier of the layer. Muste be unique. Default use a generated uuid.
   * @param {string} [options.name] Name of the layer.
   * @param {string[]} [options.copyrights] Array of copyrights.
   * @param {Array<Layer>} [options.children=[]] Sublayers, all child layers will have a parent property associated to this layer.
   * @param {boolean} [options.visible=true]  Define if the layer is currently display on the map.
   * @param {boolean} [options.disabled=false] Define if the layer is currently display on the map but can't be seen (extent, zoom ,data restrictions).
   * @param {number} [options.hitTolerance=5] Hit-detection tolerance in css pixels. Pixels inside the radius around the given position will be checked for features.
   * @param {Object} [options.properties={}] Application-specific layer properties.
   */
  constructor(options = {}) {
    super();
    this.defineProperties(options);

    this.setProperties(options.properties);

    this.visible = options.visible === undefined ? true : !!options.visible;

    this.group = options.group;

    this.copyrights = options.copyrights;

    this.children = options.children;

    // Listen for group visiblity change
    // if a layer from a group is newly visible we hide the others.
    this.on(`change:visible:group`, (evt) => {
      // We hide layer of the same group
      if (
        this.group === evt.target.group &&
        this !== evt.target &&
        this.visible
      ) {
        this.visible = false;
        // Propagate event to parent
      } else if (this.children) {
        this.children.forEach((child) => child.dispatchEvent(evt));
      }
    });
  }

  /**
   * Define layer's properties that needs custom get and set.
   *
   * @ignore
   */
  defineProperties(options) {
    const { name, key, properties, hitTolerance } = {
      ...options,
    };
    const uid = uuid();
    Object.defineProperties(this, {
      /* Layer's information properties */
      name: {
        value: name,
      },
      key: {
        value: key || uid,
      },
      group: {
        get: () => this.get('group'),
        set: (newGroup) => {
          this.set('group', newGroup);
        },
      },
      copyrights: {
        get: () => this.get('copyrights'),
        set: (newCopyrights) => {
          const arrValue =
            newCopyrights && !Array.isArray(newCopyrights)
              ? [newCopyrights]
              : newCopyrights;
          this.set('copyrights', arrValue || []);
        },
      },
      // options is used for clone function.
      options: {
        value: options,
      },
      map: {
        writable: true,
      },

      /* Layer's state properties */
      visible: {
        get: () => this.get('visible'),
        set: (newVisible) => {
          if (newVisible === this.visible) {
            return;
          }

          this.set('visible', newVisible);

          if (this.visible) {
            if (this.parent && !this.parent.visible) {
              this.parent.visible = true;
            }

            if (this.children && this.children.find((child) => child.group)) {
              const child = this.children.find((childd) => !!childd.group);
              // Make visible only radioGroup layers
              child.visible = true;
            }

            // Warn the same group that a new layer is visible
            if (this.parent && this.group) {
              // We search for the higher parent then it will dispatch to all the tree.
              let higherParent = this.parent;

              while (higherParent.parent) {
                higherParent = higherParent.parent;
              }
              higherParent.dispatchEvent({
                type: `change:visible:group`,
                target: this,
              });
            }
          } else if (!this.visible) {
            if (
              this.parent &&
              this.parent.visible &&
              !this.parent.children.find((child) => child.visible)
            ) {
              this.parent.visible = false;
            }
          }
        },
      },
      disabled: {
        get: () => this.get('disabled'),
        set: (newValue) => {
          this.set('disabled', newValue);
        },
      },

      /* Layer's hierarchy properties */
      parent: {
        value: null,
        writable: true,
      },
      children: {
        get: () => this.get('children') || [],
        set: (newValue) => {
          (this.children || []).forEach((child) => {
            // eslint-disable-next-line no-param-reassign
            child.parent = null;
          });
          if (Array.isArray(newValue)) {
            newValue.forEach((child) => {
              // eslint-disable-next-line no-param-reassign
              child.parent = this;
            });
          }
          this.set('children', newValue || []);
        },
      },

      /* Layer's query properties */
      hitTolerance: {
        value: hitTolerance || 5,
        writable: true,
      },

      /* Custom app specific properties */
      properties: {
        value: { ...(properties || {}) },
      },
    });
  }

  /**
   * Initialize the layer with the map passed in parameters.
   *
   * @param {ol/Map~Map|mapboxgl.Map} map A map.
   */
  attachToMap(map) {
    this.detachFromMap();
    /** @ignore */
    this.map = map;

    if (this.children) {
      this.children.forEach((child) => {
        child.attachToMap(map);
      });
    }
  }

  /**
   * Terminate what was initialized in init function. Remove layer, events...
   */
  // eslint-disable-next-line class-methods-use-this
  detachFromMap() {
    /** @ignore */
    this.map = null;
  }

  /**
   * Request feature information for a given coordinate.
   * This function must be implemented by inheriting layers.
   *
   * @param {ol/coordinate~Coordinate} coordinate Coordinate.
   * @param {Object} options Some options. See child classes to see which are supported.
   * @return {Promise<FeatureInfo>} An empty response.
   */
  // eslint-disable-next-line no-unused-vars,@typescript-eslint/no-unused-vars
  getFeatureInfoAtCoordinate(coordinate, options) {
    // eslint-disable-next-line no-console
    console.error(
      'getFeatureInfoAtCoordinate must be implemented by inheriting layers',
      this.key,
    );

    // This layer returns no feature info.
    // The function is implemented by inheriting layers.
    return Promise.resolve({
      layer: this,
      features: [],
      coordinate,
    });
  }

  /**
   * Return the an array containing all the descendants of the layer in a flat array. Including the current layer.
   */
  flat() {
    return getLayersAsFlatArray(this);
  }
}

