/* eslint-disable class-methods-use-this */
/* eslint-disable max-classes-per-file */

/**
 * Copyright control interface.
 *
 */
export class CopyrightInterface {
  /**
   * Return an array of layer's copyright.
   *
   * @return {String[]} A list of copyrights to render.
   */
  getCopyrights() {}
}

/**
 * Mixin for CopyrightInterface.
 *
 * @param {Class} Base  A class to extend with {CopyrightInterface} functionnalities.
 * @return {Class}  A class that implements <CopyrightInterface> class and extends Base;
 * @private
 */
const CopyrightMixin = (Base) =>
  class extends Base {
    render() {
      if (!this.element) {
        return;
      }
      this.element.innerHTML = this.active
        ? this.getCopyrights().join(' | ')
        : '';
    }

    createDefaultElement() {
      this.element = document.createElement('div');
      this.element.id = 'mbt-copyright';
      Object.assign(this.element.style, {
        position: 'absolute',
        bottom: 0,
        right: 0,
        fontSize: '.8rem',
        padding: '0 10px',
      });
    }
  };

export default CopyrightMixin;

/* eslint-disable max-classes-per-file */
import { toLonLat } from 'ol/proj';
import OLLayer from 'ol/layer/Layer';
import Source from 'ol/source/Source';
import GeoJSON from 'ol/format/GeoJSON';
import { getUrlWithParams, getMapboxMapCopyrights } from '../utils';

/**
 * MapboxLayerInterface
 */
export class MapboxLayerInterface {
  /**
   * Return the render function function for the ol layer.
   *
   */
  // eslint-disable-next-line class-methods-use-this
  getOlLayerRender() {}

  /**
   * Return the Class to instanciate for the mapbox map.
   *
   * @return {mapboxgl.Map|maplibregl.Map} map
   */
  // eslint-disable-next-line class-methods-use-this
  getMapboxMapClass() {}
}

/**
 * Mixin for MapboxLayerLayerInterface.
 * It's used to share code between Mapbox and Maplibre layers without importing both libs
 *
 * @param {Class} Base A class to extend with {MapboxLayerLayerInterface} functionnalities.
 * @return {Class}  A class that implements {MapboxLayerLayerInterface} class and extends Base;
 * @private
 */
const MapboxLayerMixin = (Base) =>
  class extends Base {
    constructor(options = {}) {
      super(options);

      this.olLayer = new OLLayer({
        source: new Source({}),
        render: this.getOlLayerRender(this),
      });

      /**
       * Url of the mapbox style.
       * @type {string}
       * @private
       */
      this.styleUrl = options.url;

      /**
       * Api key for the url of the mapbox style.
       * If set to false, the apiKey is not required.
       * @type {string}
       * @private
       */
      this.apiKey = options.apiKey;

      /**
       * Name of the apiKey to set in the url request.
       * Default is 'key'.
       * @type {string}
       * @private
       */
      this.apiKeyName = options.apiKeyName || 'key';

      /** @ignore */
      this.updateAttribution = this.updateAttribution.bind(this);
    }

    /**
     * Initialize the layer and listen to feature clicks.
     * @param {ol/Map~Map} map
     */
    attachToMap(map) {
      super.attachToMap(map);

      if (!this.map) {
        return;
      }

      /**
       * The feature format.
       * @type {ol/format/GeoJSON}
       */
      this.format = new GeoJSON({
        featureProjection: this.map.getView().getProjection(),
      });

      this.loadMbMap();
    }

    /**
     * Terminate what was initialized in init function. Remove layer, events...
     */
    detachFromMap() {
      if (this.mbMap) {
        this.mbMap.off('idle', this.updateAttribution);
        // Some asynchrone repaints are triggered even if the mbMap has been removed,
        // to avoid display of errors we set an empty function.
        this.mbMap.triggerRepaint = () => {};
        this.mbMap.remove();
        this.mbMap = null;
      }
      this.loaded = false;
      super.detachFromMap();
    }

    /**
     * Create the mapbox map.
     * @private
     */
    loadMbMap() {
      this.olListenersKeys.push(
        this.map.on('change:target', () => {
          this.loadMbMap();
        }),
      );

      if (!this.map.getTargetElement()) {
        return;
      }

      if (!this.visible) {
        // On next change of visibility we load the map
        this.olListenersKeys.push(
          this.once('change:visible', () => {
            this.loadMbMap();
          }),
        );
        return;
      }

      const container = document.createElement('div');
      container.style.position = 'absolute';
      container.style.width = '100%';
      container.style.height = '100%';

      if (!this.apiKey && !this.styleUrl.includes(this.apiKeyName)) {
        // eslint-disable-next-line no-console
        console.error(
          `No apiKey defined for mapbox layer with style url to ${this.styleUrl}`,
        );
      }

      const Map = this.getMapboxMapClass();

      /**
       * A mapbox map
       * @type {mapboxgl.Map}
       */
      this.mbMap = new Map({
        style: getUrlWithParams(this.styleUrl, {
          [this.apiKeyName]: this.apiKey,
        }).toString(),
        container,
        interactive: false,
        trackResize: false,
        attributionControl: false,
        ...(this.options.mapOptions || {}),
      });

      this.mbMap.once('load', () => {
        /**
         * Is the map loaded.
         * @type {boolean}
         */
        this.loaded = true;

        this.dispatchEvent({
          type: 'load',
          target: this,
        });
      });

      this.mbMap.on('idle', this.updateAttribution);
    }

    /**
     * Update attributions of the source.
     * @private
     */
    updateAttribution(evt) {
      const newAttributions = getMapboxMapCopyrights(evt.target) || [];
      if (this.copyrights?.toString() !== newAttributions.toString()) {
        this.copyrights = newAttributions;
        this.olLayer.getSource()?.setAttributions(newAttributions);
      }
    }

    /**
     * Request feature information for a given coordinate.
     * @param {ol/coordinate~Coordinate} coordinate Coordinate to request the information at.
     * @param {Object} options A [mapboxgl.Map#queryrenderedfeatures](https://docs.mapbox.com/mapbox-gl-js/api/map/#map#queryrenderedfeatures) options parameter.
     * @return {Promise<FeatureInfo>} Promise with features, layer and coordinate. The original Mapbox feature is available as a property named 'mapboxFeature'.
     */
    getFeatureInfoAtCoordinate(coordinate, options) {
      // Ignore the getFeatureInfo until the mapbox map is loaded
      if (
        !options ||
        !this.format ||
        !this.mbMap ||
        !this.mbMap.isStyleLoaded()
      ) {
        return Promise.resolve({ coordinate, features: [], layer: this });
      }

      let pixel = coordinate && this.mbMap.project(toLonLat(coordinate));

      if (this.hitTolerance) {
        const { x, y } = pixel;
        pixel = [
          { x: x - this.hitTolerance, y: y - this.hitTolerance },
          { x: x + this.hitTolerance, y: y + this.hitTolerance },
        ];
      }

      // At this point we get GeoJSON Mapbox feature, we transform it to an OpenLayers
      // feature to be consistent with other layers.
      const features = this.mbMap
        .queryRenderedFeatures(pixel, options)
        .map((feature) => {
          const olFeature = this.format.readFeature(feature);
          if (olFeature) {
            // We save the original mapbox feature to avoid losing informations
            // potentially needed for other functionnality like highlighting
            // (id, layer id, source, sourceLayer ...)
            olFeature.set('mapboxFeature', feature);
          }
          return olFeature;
        });

      return Promise.resolve({
        layer: this,
        features,
        coordinate,
      });
    }
  };

export default MapboxLayerMixin;

/* eslint-disable no-empty-function,@typescript-eslint/no-empty-function */
/* eslint-disable no-useless-constructor,@typescript-eslint/no-useless-constructor */
/* eslint-disable no-unused-vars,@typescript-eslint/no-unused-vars */
/* eslint-disable class-methods-use-this */
/* eslint-disable max-classes-per-file */
import { buffer, containsCoordinate, intersects } from 'ol/extent';
import { unByKey } from 'ol/Observable';
import GeoJSON from 'ol/format/GeoJSON';
import debounce from 'lodash.debounce';
import throttle from 'lodash.throttle';
import { fromLonLat } from 'ol/proj';
import realtimeDefaultStyle from '../styles/realtimeDefaultStyle';
import { RealtimeAPI, RealtimeModes } from '../../api';
import renderTrajectories from '../utils/renderTrajectories';
import * as trackerConfig from '../utils/trackerConfig';

/**
 * RealtimeLayerInterface.
 */
export class RealtimeLayerInterface {
  /*
   * Constructor

   * @param {Object} options Layer options.
   * @param {string} options.url Realtime service url.
   * @param {string} options.apiKey Access key for [geOps services](https://developer.geops.io/).
   * @param {boolean} [options.debug=false] Display additional debug informations.
   * @param {RealtimeMode} [options.mode=RealtimeMode.TOPOGRAPHIC] Realtime's Mode.
   * @param {number} [options.minZoomInterpolation=8] Minimal zoom when trains positions start to be interpolated.
   * @param {number} [options.minZoomNonTrain=9] Minimal zoom when non trains vehicles are allowed to be displayed.
   */
  constructor(options = {}) {}

  /**
   * Initialize the layer subscribing to the Realtime api.
   *
   * @param {ol/Map~Map} map
   */
  attachToMap(map) {}

  /**
   * Terminate the layer unsubscribing to the Realtime api.
   */
  detachFromMap() {}

  /**
   * Start the clock.
   */
  start() {}

  /**
   * Stop the clock.
   */
  stop() {}

  /**
   * Set the Realtime api's bbox.
   *
   * @param {Array<number>} extent  Extent to request, [minX, minY, maxX, maxY, zoom].
   * @param {number} zoom  Zoom level to request. Must be an integer.
   */
  setBbox(extent, zoom) {}

  /**
   * Set the Realtime api's mode.
   *
   * @param {RealtimeMode} mode  Realtime mode
   */
  setMode(mode) {}

  /**
   * Request the stopSequence and the fullTrajectory informations for a vehicle.
   *
   * @param {string} id The vehicle identifier (the  train_id property).
   * @param {RealtimeMode} mode The mode to request. If not defined, the layer´s mode propetrty will be used.
   * @return {Promise<{stopSequence: StopSequence, fullTrajectory: FullTrajectory>} A promise that will be resolved with the trajectory informations.
   */
  getTrajectoryInfos(id, mode) {}
}

/**
 * Mixin for RealtimeLayerInterface.
 *
 * @param {Class} Base A class to extend with {RealtimeLayerInterface} functionnalities.
 * @return {Class}  A class that implements {RealtimeLayerInterface} class and extends Base;
 * @private
 */
const RealtimeLayerMixin = (Base) =>
  class extends Base {
    constructor(options = {}) {
      super({
        hitTolerance: 10,
        ...options,
      });

      this.debug = options.debug;
      this.mode = options.mode || RealtimeModes.TOPOGRAPHIC;
      this.api = options.api || new RealtimeAPI(options);
      this.tenant = options.tenant || ''; // sbb,sbh or sbm
      this.minZoomNonTrain = options.minZoomNonTrain || 9; // Min zoom level from which non trains are allowed to be displayed. Min value is 9 (as configured by the server
      this.minZoomInterpolation = options.minZoomInterpolation || 8; // Min zoom level from which trains positions are not interpolated.
      this.format = new GeoJSON();
      this.generalizationLevelByZoom = options.generalizationLevelByZoom || [
        5, 5, 5, 5, 5, 5, 5, 5, 10, 30, 30, 100, 100, 100,
      ];
      this.getGeneralizationLevelByZoom = (zoom) => {
        return (
          (options.getGeneralizationLevelByZoom &&
            options.getGeneralizationLevelByZoom(
              zoom,
              this.generalizationLevelByZoom,
            )) ||
          this.generalizationLevelByZoom[zoom]
        );
      };

      this.renderTimeIntervalByZoom = options.renderTimeIntervalByZoom || [
        100000, 50000, 40000, 30000, 20000, 15000, 10000, 5000, 2000, 1000, 400,
        300, 250, 180, 90, 60, 50, 50, 50, 50, 50,
      ];

      this.getRenderTimeIntervalByZoom = (zoom) => {
        return (
          (options.getRenderTimeIntervalByZoom &&
            options.getRenderTimeIntervalByZoom(
              zoom,
              this.renderTimeIntervalByZoom,
            )) ||
          this.renderTimeIntervalByZoom[zoom]
        );
      };

      // This property will call api.setBbox on each movend event
      this.isUpdateBboxOnMoveEnd = options.isUpdateBboxOnMoveEnd !== false;

      // Define throttling and debounce render function
      this.throttleRenderTrajectories = throttle(
        this.renderTrajectoriesInternal,
        50,
        { leading: false, trailing: true },
      );

      this.debounceRenderTrajectories = debounce(
        this.renderTrajectoriesInternal,
        50,
        { leading: true, trailing: true, maxWait: 5000 },
      );

      // Bind callbacks
      this.onFeatureHover = this.onFeatureHover.bind(this);
      this.onFeatureClick = this.onFeatureClick.bind(this);
      this.renderTrajectoriesInternal =
        this.renderTrajectoriesInternal.bind(this);
      this.onTrajectoryMessage = this.onTrajectoryMessage.bind(this);
      this.onDeleteTrajectoryMessage =
        this.onDeleteTrajectoryMessage.bind(this);
      this.onDocumentVisibilityChange =
        this.onDocumentVisibilityChange.bind(this);
    }

    /**
     * Define layer's properties.
     *
     * @ignore
     */
    defineProperties(options) {
      const {
        style,
        speed,
        pixelRatio,
        hoverVehicleId,
        selectedVehicleId,
        filter,
        sort,
        time,
        live,
        canvas,
        styleOptions,
      } = options;

      let currSpeed = speed || 1;
      let currTime = time || new Date();

      super.defineProperties(options);

      Object.defineProperties(this, {
        isTrackerLayer: { value: true },

        canvas: {
          value: canvas || document.createElement('canvas'),
        },

        /**
         * Style function used to render a vehicle.
         */
        style: {
          value: style || realtimeDefaultStyle,
        },

        /**
         * Custom options to pass as last parameter of the style function.
         */
        styleOptions: {
          value: { ...trackerConfig, ...(styleOptions || {}) },
        },

        /**
         * Speed of the wheel of time.
         * If live property is true. The speed is ignored.
         */
        speed: {
          get: () => currSpeed,
          set: (newSpeed) => {
            currSpeed = newSpeed;
            this.start();
          },
        },

        /**
         * Function to filter which vehicles to display.
         */
        filter: {
          value: filter,
          writable: true,
        },

        /**
         * Function to sort the vehicles to display.
         */
        sort: {
          value: sort,
          writable: true,
        },

        /**
         * If true. The layer will always use Date.now() on the next tick to render the trajectories.
         * When true, setting the time property has no effect.
         */
        live: {
          value: live === false ? live : true,
          writable: true,
        },

        /**
         * Time used to display the trajectories. Can be a Date or a number in ms representing a Date.
         * If live property is true. The setter does nothing.
         */
        time: {
          get: () => currTime,
          set: (newTime) => {
            currTime = newTime && newTime.getTime ? newTime : new Date(newTime);
            this.renderTrajectories();
          },
        },

        /**
         * Keep track of which trajectories are stored.
         */
        trajectories: {
          value: {},
          writable: true,
        },

        /**
         * Id of the hovered vehicle.
         */
        hoverVehicleId: {
          value: hoverVehicleId,
          writable: true,
        },

        /**
         * Id of the selected vehicle.
         */
        selectedVehicleId: {
          value: selectedVehicleId,
          writable: true,
        },

        /**
         * Id of the selected vehicle.
         */
        pixelRatio: {
          value:
            pixelRatio ||
            (typeof window !== 'undefined' ? window.devicePixelRatio : 1),
          writable: true,
        },

        /**
         * If true, encapsulates the renderTrajectories calls in a requestAnimationFrame.
         */
        useRequestAnimationFrame: {
          value: options.useRequestAnimationFrame || false,
          writable: true,
        },

        /**
         * If true, encapsulates the renderTrajectories calls in a throttle function. Default to true.
         */
        useThrottle: {
          value: options.useThrottle !== false,
          writable: true,
        },

        /**
         * If true, encapsulates the renderTrajectories calls in a debounce function.
         */
        useDebounce: {
          value: options.useDebounce || false,
          writable: true,
        },

        /**
         * Debug properties.
         */
        // Not used anymore, but could be useful for debugging.
        // showVehicleTraj: {
        //   value:
        //     options.showVehicleTraj !== undefined
        //       ? options.showVehicleTraj
        //       : true,
        //   writable: true,
        // },
      });
    }

    attachToMap(map) {
      super.attachToMap(map);

      // If the layer is visible we start  the rendering clock
      if (this.visible) {
        this.start();
      }

      // On change of visibility we start/stop the rendering clock
      this.visibilityRef = this.on('change:visible', (evt) => {
        if (evt.target.visible) {
          this.start();
        } else {
          this.stop();
        }
      });

      // To avoid browser hanging when the tab is not visible for a certain amount of time,
      // We stop the rendering and the websocket when hide and start again when show.
      document.addEventListener(
        'visibilitychange',
        this.onDocumentVisibilityChange,
      );
    }

    detachFromMap() {
      document.removeEventListener(
        'visibilitychange',
        this.onDocumentVisibilityChange,
      );

      this.stop();
      unByKey(this.visibilityRef);
      const context = this.canvas.getContext('2d');
      context.clearRect(0, 0, this.canvas.width, this.canvas.height);
      super.detachFromMap();
    }

    start() {
      this.stop();
      this.renderTrajectories();
      this.startUpdateTime();

      this.api.open();
      this.api.subscribeTrajectory(
        this.mode,
        this.onTrajectoryMessage,
        this.isUpdateBboxOnMoveEnd,
      );
      this.api.subscribeDeletedVehicles(
        this.mode,
        this.onDeleteTrajectoryMessage,
        this.isUpdateBboxOnMoveEnd,
      );

      if (this.isUpdateBboxOnMoveEnd) {
        // Update the bbox on each move end
        this.setBbox();
      }
    }

    /**
     * Start the clock.
     * @private
     */
    startUpdateTime() {
      this.stopUpdateTime();
      this.updateTimeDelay = this.getRefreshTimeInMs();
      this.updateTimeInterval = setInterval(() => {
        // When live=true, we update the time with new Date();
        this.time = this.live
          ? new Date()
          : this.time.getTime() + this.updateTimeDelay * this.speed;
      }, this.updateTimeDelay);
    }

    stop() {
      this.api.unsubscribeTrajectory(this.onTrajectoryMessage);
      this.api.unsubscribeDeletedVehicles(this.onDeleteTrajectoryMessage);
      this.api.close();
    }

    /**
     * Stop the clock.
     * @private
     */
    stopUpdateTime() {
      if (this.updateTimeInterval) {
        clearInterval(this.updateTimeInterval);
      }
    }

    /**
     * Launch renderTrajectories. it avoids duplicating code in renderTrajectories method.
     *
     * @param {object} viewState The view state of the map.
     * @param {number[2]} viewState.center Center coordinate of the map in mercator coordinate.
     * @param {number[4]} viewState.extent Extent of the map in mercator coordinates.
     * @param {number[2]} viewState.size Size ([width, height]) of the canvas to render.
     * @param {number} [viewState.rotation = 0] Rotation of the map to render.
     * @param {number} viewState.resolution Resolution of the map to render.
     * @param {boolean} noInterpolate If true trajectories are not interpolated but
     *   drawn at the last known coordinate. Use this for performance optimization
     *   during map navigation.
     * @private
     */
    renderTrajectoriesInternal(viewState, noInterpolate) {
      if (!this.map) {
        return false;
      }
      const time = this.live ? Date.now() : this.time;

      const trajectories = Object.values(this.trajectories);

      // console.time('sort');
      if (this.sort) {
        trajectories.sort(this.sort);
      }
      // console.timeEnd('sort');

      // console.time('render');
      this.renderState = renderTrajectories(
        this.canvas,
        trajectories,
        this.style,
        { ...viewState, pixelRatio: this.pixelRatio, time },
        {
          noInterpolate:
            viewState.zoom < this.minZoomInterpolation ? true : noInterpolate,
          hoverVehicleId: this.hoverVehicleId,
          selectedVehicleId: this.selectedVehicleId,
          ...this.styleOptions,
        },
      );

      // console.timeEnd('render');
      return true;
    }

    /**
     * Render the trajectories requesting an animation frame and cancelling the previous one.
     * This function must be overrided by children to provide the correct parameters.
     *
     * @param {object} viewState The view state of the map.
     * @param {number[2]} viewState.center Center coordinate of the map in mercator coordinate.
     * @param {number[4]} viewState.extent Extent of the map in mercator coordinates.
     * @param {number[2]} viewState.size Size ([width, height]) of the canvas to render.
     * @param {number} [viewState.rotation = 0] Rotation of the map to render.
     * @param {number} viewState.resolution Resolution of the map to render.
     * @param {boolean} noInterpolate If true trajectories are not interpolated but
     *   drawn at the last known coordinate. Use this for performance optimization
     *   during map navigation.
     * @private
     */
    renderTrajectories(viewState, noInterpolate) {
      if (this.requestId) {
        cancelAnimationFrame(this.requestId);
        this.requestId = null;
      }

      if (!noInterpolate && this.useRequestAnimationFrame) {
        this.requestId = requestAnimationFrame(() => {
          this.renderTrajectoriesInternal(viewState, noInterpolate);
        });
      } else if (!noInterpolate && this.useDebounce) {
        this.debounceRenderTrajectories(viewState, noInterpolate);
      } else if (!noInterpolate && this.useThrottle) {
        this.throttleRenderTrajectories(viewState, noInterpolate);
      } else {
        this.renderTrajectoriesInternal(viewState, noInterpolate);
      }
    }

    setBbox(extent, zoom) {
      // Clean trajectories before sending the new bbox
      // Purge trajectories:
      // - which are outside the extent
      // - when it's bus and zoom level is too low for them
      const keys = Object.keys(this.trajectories);
      for (let i = keys.length - 1; i >= 0; i -= 1) {
        this.purgeTrajectory(this.trajectories[keys[i]], extent, zoom);
      }

      const bbox = [...extent];

      if (this.isUpdateBboxOnMoveEnd) {
        bbox.push(zoom);

        if (this.tenant) {
          bbox.push(`tenant=${this.tenant}`);
        }

        /* @ignore */
        this.generalizationLevel = this.getGeneralizationLevelByZoom(zoom);
        if (this.generalizationLevel) {
          bbox.push(`gen=${this.generalizationLevel}`);
        }
      }

      this.api.bbox = bbox;
    }

    setMode(mode) {
      if (this.mode === mode) {
        return;
      }
      this.mode = mode;
      this.api.subscribeTrajectory(
        this.mode,
        this.onTrajectoryMessage,
        this.isUpdateBboxOnMoveEnd,
      );
      this.api.subscribeDeletedVehicles(
        this.mode,
        this.onDeleteTrajectoryMessage,
        this.isUpdateBboxOnMoveEnd,
      );
    }

    /**
     * Get the duration before the next update depending on zoom level.
     *
     * @private
     * @param {number} zoom
     */
    getRefreshTimeInMs(zoom) {
      const roundedZoom = Math.round(zoom);
      const timeStep = this.getRenderTimeIntervalByZoom(roundedZoom) || 25;
      const nextTick = Math.max(25, timeStep / this.speed);
      const nextThrottleTick = Math.min(nextTick, 500);
      // TODO: see if this should go elsewhere.
      if (this.useThrottle) {
        this.throttleRenderTrajectories = throttle(
          this.renderTrajectoriesInternal,
          nextThrottleTick,
          { leading: true, trailing: true },
        );
      } else if (this.useDebounce) {
        this.debounceRenderTrajectories = debounce(
          this.renderTrajectoriesInternal,
          nextThrottleTick,
          { leading: true, trailing: true, maxWait: 5000 },
        );
      }
      if (this.api?.buffer) {
        const [, size] = this.api.buffer;
        this.api.buffer = [nextThrottleTick, size];
      }
      return nextTick;
    }

    /**
     * Get vehicle.
     * @param {function} filterFc A function use to filter results.
     * @return {Array<Object>} Array of vehicle.
     */
    getVehicle(filterFc) {
      return Object.values(this.trajectories).filter(filterFc);
    }

    /**
     * Request feature information for a given coordinate.
     *
     * @param {ol/coordinate~Coordinate} coordinate Coordinate.
     * @param {Object} options Options See child classes to see which options are supported.
     * @param {number} [options.resolution=1] The resolution of the map.
     * @param {number} [options.nb=Infinity] The max number of vehicles to return.
     * @return {Promise<FeatureInfo>} Promise with features, layer and coordinate.
     */
    getFeatureInfoAtCoordinate(coordinate, options = {}) {
      const { resolution, nb } = options;
      const ext = buffer(
        [...coordinate, ...coordinate],
        this.hitTolerance * resolution,
      );
      let trajectories = Object.values(this.trajectories);

      if (this.sort) {
        trajectories = trajectories.sort(this.sort);
      }

      const vehicles = [];
      for (let i = 0; i < trajectories.length; i += 1) {
        if (
          trajectories[i].properties.coordinate &&
          containsCoordinate(ext, trajectories[i].properties.coordinate)
        ) {
          vehicles.push(trajectories[i]);
        }
        if (vehicles.length === nb) {
          break;
        }
      }

      return Promise.resolve({
        layer: this,
        features: vehicles.map((vehicle) => this.format.readFeature(vehicle)),
        coordinate,
      });
    }

    /**
     * Request the stopSequence and the fullTrajectory informations for a vehicle.
     *
     * @param {string} id The vehicle identifier (the  train_id property).
     * @return {Promise<{stopSequence: StopSequence, fullTrajectory: FullTrajectory>} A promise that will be resolved with the trajectory informations.
     */
    getTrajectoryInfos(id) {
      // When a vehicle is selected, we request the complete stop sequence and the complete full trajectory.
      // Then we combine them in one response and send them to inherited layers.
      const promises = [
        this.api.getStopSequence(id, this.mode),
        this.api.getFullTrajectory(id, this.mode, this.generalizationLevel),
      ];

      return Promise.all(promises).then(([stopSequence, fullTrajectory]) => {
        const response = {
          stopSequence,
          fullTrajectory,
        };
        return response;
      });
    }

    /**
     * Determine if the trajectory is useless and should be removed from the list or not.
     * By default, this function exclude vehicles:
     *  - that have their trajectory outside the current extent and
     *  - that are not a train and zoom level is lower than layer's minZoomNonTrain property.
     *
     * @param {RealtimeTrajectory} trajectory
     * @param {Array<number>} extent
     * @param {number} zoom
     * @return {boolean} if the trajectory must be displayed or not.
     * @ignore
     */
    purgeTrajectory(trajectory, extent, zoom) {
      const { type, bounds, train_id: id } = trajectory.properties;
      if (
        !intersects(extent, bounds) ||
        (type !== 'rail' && zoom < (this.minZoomNonTrain || 9))
      ) {
        this.removeTrajectory(id);
        return true;
      }
      return false;
    }

    /**
     * Add a trajectory.
     * @param {RealtimeTrajectory} trajectory The trajectory to add.
     * @private
     */
    addTrajectory(trajectory) {
      if (this.filter && !this.filter(trajectory)) {
        return;
      }
      this.trajectories[trajectory.properties.train_id] = trajectory;
      this.renderTrajectories();
    }

    removeTrajectory(id) {
      delete this.trajectories[id];
    }

    /**
     * On zoomend we adjust the time interval of the update of vehicles positions.
     *
     * @param evt Event that triggered the function.
     * @private
     */
    // eslint-disable-next-line no-unused-vars
    onZoomEnd(evt) {
      this.startUpdateTime();
    }

    onDocumentVisibilityChange() {
      if (!this.visible) {
        return;
      }
      if (document.hidden) {
        this.stop();
      } else {
        this.start();
      }
    }

    /**
     * Callback on websocket's trajectory channel events.
     * It adds a trajectory to the list.
     *
     * @private
     */
    onTrajectoryMessage(data) {
      if (!data.content) {
        return;
      }
      const trajectory = data.content;

      const {
        geometry,
        properties: {
          train_id: id,
          time_since_update: timeSinceUpdate,
          raw_coordinates: rawCoordinates,
        },
      } = trajectory;

      // ignore old events [SBAHNM-97]
      if (timeSinceUpdate < 0) {
        return;
      }

      // console.time(`onTrajectoryMessage${data.content.properties.train_id}`);
      if (this.purgeTrajectory(trajectory)) {
        return;
      }

      if (
        this.debug &&
        this.mode === RealtimeModes.TOPOGRAPHIC &&
        rawCoordinates
      ) {
        trajectory.properties.olGeometry = {
          type: 'Point',
          coordinates: fromLonLat(
            rawCoordinates,
            this.map.getView().getProjection(),
          ),
        };
      } else {
        trajectory.properties.olGeometry = this.format.readGeometry(geometry);
      }

      // TODO Make sure the timeOffset is useful. May be we can remove it.
      trajectory.properties.timeOffset = Date.now() - data.timestamp;
      this.addTrajectory(trajectory);
    }

    /**
     * Callback on websocket's deleted_vehicles channel events.
     * It removes the trajectory from the list.
     *
     * @private
     * @override
     */
    onDeleteTrajectoryMessage(data) {
      if (!data.content) {
        return;
      }

      this.removeTrajectory(data.content);
    }

    /**
     * Callback when user moves the mouse/pointer over the map.
     * It sets the layer's hoverVehicleId property with the current hovered vehicle's id.
     *
     * @private
     * @override
     */
    onFeatureHover(features, layer, coordinate) {
      const [feature] = features;
      let id = null;
      if (feature) {
        id = feature.get('train_id');
      }
      if (this.hoverVehicleId !== id) {
        /** @ignore */
        this.hoverVehicleId = id;
        this.renderTrajectories(true);
      }
    }

    /**
     * Callback when user clicks on the map.
     * It sets the layer's selectedVehicleId property with the current selected vehicle's id.
     *
     * @private
     * @override
     */
    onFeatureClick(features, layer, coordinate) {
      const [feature] = features;
      let id = null;
      if (feature) {
        id = feature.get('train_id');
      }
      if (this.selectedVehicleId !== id) {
        /** @ignore */
        this.selectedVehicleId = id;
        this.selectedVehicle = feature;
        this.renderTrajectories(true);
      }
    }
  };

export default RealtimeLayerMixin;

/* eslint-disable no-empty-function */
/* eslint-disable no-useless-constructor */
/* eslint-disable class-methods-use-this */
/* eslint-disable max-classes-per-file */
import { StopsAPI } from '../../api';

/**
 * StopFinder control interface.
 *
 * @classproperty {StopsSearchParams} apiParams - Default request parameters used by the search method. See [Stops service documentation](https://developer.geops.io/apis/5dcbd702a256d90001cf1361/).
 */
export class StopFinderInterface {
  /**
   * Constructor.
   *
   * @param {Object} options Map options
   * @param {string} options.apiKey Access key for [geOps services](https://developer.geops.io/). See StopsAPI.
   * @param {string} [options.url='https://api.geops.io/tracker/v1'] Stops service url. See StopsAPI.
   * @param {string} [options.placeholder='Search for a stop...'] Input field placeholder.
   * @param {StopsSearchParams} [options.apiParams={ limit: 20 }] Request parameters. See [Stops service documentation](https://developer.geops.io/apis/5dcbd702a256d90001cf1361/).
   */
  // eslint-disable-next-line no-unused-vars, @typescript-eslint/no-useless-constructor, @typescript-eslint/no-unused-vars, @typescript-eslint/no-empty-function
  constructor(options = {}) {}

  /**
   * Launch a search.
   *
   * @param {String} query The query to search for.
   * @param {AbortController} abortController Abort controller used to cancel the request.
   * @return {Promise<Array<GeoJSONFeature>>} An array of GeoJSON features with coordinates in [EPSG:4326](http://epsg.io/4326).
   */
  // eslint-disable-next-line no-unused-vars, @typescript-eslint/no-unused-vars
  search(query, abortController) {}
}

/**
 * Mixin for StopFinderInterface.
 *
 * @param {Class} Base  A class to extend with {StopFinderInterface} functionnalities.
 * @return {Class}  A class that implements <StopFinderInterface> class and extends Base;
 * @private
 */
const StopFinderMixin = (Base) =>
  class extends Base {
    constructor(options = {}) {
      super(options);
      const { apiParams, apiKey, url } = options;

      this.apiParams = { limit: 20, ...(apiParams || {}) };
      this.placeholder = options.placeholder || 'Search for a stop...';

      const apiOptions = { apiKey };
      if (url) {
        apiOptions.url = url;
      }
      this.api = new StopsAPI(apiOptions);
      this.abortController = new AbortController();
    }

    render(featureCollection) {
      const suggestions = featureCollection?.features || [];
      if (!this.suggestionsElt) {
        return;
      }

      this.suggestionsElt.style.display = suggestions.length ? 'block' : 'none';

      this.suggestionsElt.innerHTML = '';

      suggestions.forEach((suggestion) => {
        const { properties } = suggestion;
        const suggElt = document.createElement('div');
        suggElt.innerHTML = properties.name;
        suggElt.onclick = () => {
          this.onSuggestionClick(suggestion);
        };
        Object.assign(suggElt.style, {
          padding: '5px 12px',
        });
        this.suggestionsElt.appendChild(suggElt);
      });
    }

    createDefaultElement() {
      /**
       * Define a default element.
       */
      this.element = document.createElement('div');
      this.element.id = 'mbt-search';
      Object.assign(this.element.style, {
        position: 'absolute',
        top: 0,
        left: '50px',
        margin: '10px',
        display: 'flex',
        flexDirection: 'column',
        width: '320px',
      });

      // Create input element
      this.inputElt = document.createElement('input');
      this.inputElt.type = 'text';
      this.inputElt.placeholder = this.placeholder;
      this.inputElt.autoComplete = 'off';
      this.inputElt.onkeyup = (evt) => {
        this.abortController.abort();
        this.abortController = new AbortController();
        this.search(evt.target.value, this.abortController);
      };
      Object.assign(this.inputElt.style, {
        padding: '10px 30px 10px 10px',
      });
      this.element.appendChild(this.inputElt);

      // Create suggestions list element
      this.suggestionsElt = document.createElement('div');
      Object.assign(this.suggestionsElt.style, {
        backgroundColor: 'white',
        overflowY: 'auto',
        cursor: 'pointer',
      });
      this.element.appendChild(this.suggestionsElt);

      this.clearElt = document.createElement('div');
      Object.assign(this.clearElt.style, {
        display: 'none',
        position: 'absolute',
        right: '0',
        padding: '0 10px',
        fontSize: '200%',
        cursor: 'pointer',
      });
      this.clearElt.innerHTML = '×';
      this.clearElt.onclick = () => this.clear();
      this.element.appendChild(this.clearElt);
    }

    search(q, abortController) {
      if (q !== undefined || q !== null) {
        this.apiParams.q = q;
      }

      if (this.clearElt) {
        this.clearElt.style.display = 'block';
      }

      return this.api
        .search(this.apiParams, abortController)
        .then((data) => {
          this.render(data);
        })
        .catch(() => {
          this.render();
        });
    }

    /**
     * To be defined in inherited class
     */
    // eslint-disable-next-line no-unused-vars, @typescript-eslint/no-unused-vars
    onSuggestionClick(suggestion) {}

    /**
     * Clear the search field and close the control.
     */
    clear() {
      if (!this.suggestionsElt) {
        return;
      }

      this.inputElt.value = '';
      this.suggestionsElt.innerHTML = '';
      this.clearElt.style.display = 'none';
    }
  };

export default StopFinderMixin;

/* eslint-disable no-empty-function,@typescript-eslint/no-empty-function */
/* eslint-disable no-useless-constructor,@typescript-eslint/no-useless-constructor */
/* eslint-disable no-unused-vars,@typescript-eslint/no-unused-vars */
/* eslint-disable class-methods-use-this */
/* eslint-disable max-classes-per-file */
import { fromLonLat } from 'ol/proj';
import { unByKey } from 'ol/Observable';

/**
 * UserInteractionsLayerInterface.
 */
export class UserInteractionsLayerInterface {
  /*
   * Constructor

   * @param {Object} options Layer options.
   * @param {string} options.userInteractions If true, it listens for user mouse hover and click event.
   * @param {string} options.userClickInteractions If true, it listens for user click event.
   * @param {string} options.userHoverInteractions If true, it listens for user mouse over event.
   * @param {string} options.defaultUserInteractions  If true, it adds default listeners for user mouse hover and click event.
   */
  constructor(options = {}) {}

  /**
   * Initialize the layer adding user interactions.
   *
   * @param {ol/Map~Map} map
   */
  attachToMap(map) {}

  /**
   * Terminate the layer unsubscribing user interactions.
   */
  detachFromMap() {}

  /**
   * Activate map listeners events.
   */
  activateUserInteractions() {}

  /**
   * Deactivaet map listeners events.
   */
  deactivateUserInteractions() {}

  /**
   * Terminate the layer unsubscribing user interactions.
   */
  onClick(callback) {}

  /**
   * Terminate the layer unsubscribing user interactions.
   */
  onHover(callback) {}
}

/**
 * Mixin for UserInteractionsLayerInterface. It provide onClick and onHover functions.
 *
 * @param {Class} Base A class to extend with {UserInteractionsLayerInterface} functionnalities.
 * @return {Class}  A class that implements {UserInteractionsLayerInterface} class and extends Base;
 * @private
 */
const UserInteractionsLayerMixin = (Base) =>
  class extends Base {
    constructor(options = {}) {
      super(options);

      const {
        userInteractions = true,
        userClickInteractions = true,
        userHoverInteractions = true,
        defaultUserInteractions = true,
      } = options;

      this.userInteractions = userInteractions;
      this.userClickInteractions = userClickInteractions;
      this.userHoverInteractions = userHoverInteractions;
      this.defaultUserInteractions = defaultUserInteractions;
      this.userClickCallbacks = [];
      this.userHoverCallbacks = [];
      this.userClickEventsKeys = [];
      this.userHoverEventsKeys = [];
      this.onUserClickCallback = this.onUserClickCallback.bind(this);
      this.onUserMoveCallback = this.onUserMoveCallback.bind(this);

      // Add mouse event callbacks
      const { onClick, onHover } = options;

      if (this.userInteractions && this.userClickInteractions && onClick) {
        this.onClick(onClick);
      }

      if (this.userInteractions && this.userHoverInteractions && onHover) {
        this.onHover(onHover);
      }
    }

    attachToMap(map, options) {
      super.attachToMap(map, options);

      if (
        this.userInteractions &&
        this.defaultUserInteractions &&
        this.userClickInteractions &&
        this.onFeatureClick
      ) {
        this.onClick(this.onFeatureClick);
      }

      if (
        this.userInteractions &&
        this.defaultUserInteractions &&
        this.userHoverInteractions &&
        this.onFeatureHover
      ) {
        this.onHover(this.onFeatureHover);
      }
      this.listenEvents();
    }

    detachFromMap() {
      this.unlistenEvents();
      super.detachFromMap();
    }

    listenEvents() {
      this.unlistenEvents();
      this.userClickCallbacks.forEach((callback) => {
        this.userClickEventsKeys.push(
          this.on(
            'user:click',
            ({ target: { features, layer, coordinate } }) => {
              callback(features, layer, coordinate);
            },
          ),
        );
      });
      this.userHoverCallbacks.forEach((callback) => {
        this.userHoverEventsKeys.push(
          this.on(
            'user:hover',
            ({ target: { features, layer, coordinate, event } }) => {
              callback(features, layer, coordinate, event);
            },
          ),
        );
      });
    }

    unlistenEvents() {
      unByKey(this.userClickEventsKeys);
      unByKey(this.userHoverEventsKeys);
      this.userClickEventsKeys = [];
      this.userHoverEventsKeys = [];
    }

    /**
     * Listens to click events on the layer.
     * @param {function} callback Callback function, called with the clicked
     *   features,
     *   the layer instance and the click event.
     */
    onClick(callback) {
      this.userClickCallbacks.push(callback);
      this.activateUserInteractions();
      if (this.map) {
        // If the layer is already attached to the map we reload the events
        this.listenEvents();
      }
    }

    /**
     * Listens to hover events on the layer.
     * @param {function} callback Callback function, called with the clicked
     *   features, the layer instance and the click event.
     */
    onHover(callback) {
      this.userHoverCallbacks.push(callback);
      this.activateUserInteractions();
      if (this.map) {
        // If the layer is already attached to the map we reload the events
        this.listenEvents();
      }
    }

    /**
     * Function triggered when the user click the map.
     * @private
     */
    onUserClickCallback(evt) {
      const coordinate = evt.coordinate || fromLonLat(evt.lngLat.toArray());
      const emptyFeatureInfo = {
        features: [],
        layer: this,
        coordinate,
        event: evt,
      };
      return this.getFeatureInfoAtCoordinate(coordinate)
        .then((featureInfo) => {
          this.dispatchEvent({
            type: 'user:click',
            target: featureInfo,
          });
          return featureInfo;
        })
        .catch(() => emptyFeatureInfo);
    }

    /**
     * Function triggered when the user move the cursor.
     * @private
     */
    onUserMoveCallback(evt) {
      const coordinate = evt.coordinate || fromLonLat(evt.lngLat.toArray());
      const emptyFeatureInfo = {
        features: [],
        layer: this,
        coordinate,
        event: evt,
      };

      return this.getFeatureInfoAtCoordinate(coordinate)
        .then((featureInfo) => {
          this.dispatchEvent({
            type: 'user:hover',
            target: featureInfo,
          });
          return featureInfo;
        })
        .catch(() => emptyFeatureInfo);
    }

    activateUserInteractions() {}

    deactivateUserInteractions() {}
  };

export default UserInteractionsLayerMixin;

export { default as realtimeDefaultStyle } from './realtimeDefaultStyle';
export { default as realtimeDelayStyle } from './realtimeDelayStyle';
export { default as realtimeSimpleStyle } from './realtimeSimpleStyle';
export * from './realtimeDefaultStyle';

import createCanvas from '../utils/createCanvas';

/** @private */
const cacheDelayBg = {};

/**
 * Draw circle delay background
 *
 * @private
 */
export const getDelayBgCanvas = (origin, radius, color) => {
  const key = `${origin}, ${radius}, ${color}`;
  if (!cacheDelayBg[key]) {
    const canvas = createCanvas(origin * 2, origin * 2);
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.beginPath();
      ctx.arc(origin, origin, radius, 0, 2 * Math.PI, false);
      ctx.fillStyle = color;
      ctx.filter = 'blur(1px)';
      ctx.fill();
      cacheDelayBg[key] = canvas;
    }
  }
  return cacheDelayBg[key];
};

/** @private */
const cacheDelayText = {};

/**
 * Draw delay text
 *
 * @private
 */
export const getDelayTextCanvas = (
  width,
  text,
  fontSize,
  font,
  delayColor,
  delayOutlineColor = '#000',
  pixelRatio = 1,
) => {
  const key = `${width}, ${text}, ${font}, ${delayColor}, ${delayOutlineColor}, ${pixelRatio}`;
  if (!cacheDelayText[key]) {
    const canvas = createCanvas(width, fontSize + 8 * pixelRatio);
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.textAlign = 'left';
      ctx.textBaseline = 'middle';
      ctx.font = font;
      ctx.fillStyle = delayColor;
      ctx.strokeStyle = delayOutlineColor;
      ctx.lineWidth = 1.5 * pixelRatio;
      const delayText = text;
      ctx.strokeText(delayText, 0, fontSize);
      ctx.fillText(delayText, 0, fontSize);
      cacheDelayText[key] = canvas;
    }
  }
  return cacheDelayText[key];
};

/** @private */
const cacheCircle = {};

/**
 * Draw colored circle with black border
 *
 * @private
 */
export const getCircleCanvas = (
  origin,
  radius,
  color,
  hasStroke,
  hasDash,
  pixelRatio,
) => {
  const key = `${origin}, ${radius}, ${color}, ${hasStroke},  ${hasDash}, ${pixelRatio}`;
  if (!cacheCircle[key]) {
    const canvas = createCanvas(origin * 2, origin * 2);
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.fillStyle = color;

      if (hasStroke) {
        ctx.lineWidth = 1 * pixelRatio;
        ctx.strokeStyle = '#000000';
      }

      ctx.beginPath();
      ctx.arc(origin, origin, radius, 0, 2 * Math.PI, false);
      ctx.fill();

      if (hasDash) {
        ctx.setLineDash([5, 3]);
      }

      if (hasStroke) {
        ctx.stroke();
      }

      cacheCircle[key] = canvas;
    }
  }
  return cacheCircle[key];
};

/** @private */
const cacheText = {};

/**
 * Draw text in the circle
 *
 * @private
 */
export const getTextCanvas = (
  text,
  origin,
  textSize,
  fillColor,
  strokeColor,
  hasStroke,
  pixelRatio,
) => {
  const key = `${text}, ${origin}, ${textSize}, ${fillColor},${strokeColor}, ${hasStroke}, ${pixelRatio}`;
  if (!cacheText[key]) {
    const canvas = createCanvas(origin * 2, origin * 2);
    if (canvas) {
      const ctx = canvas.getContext('2d');

      // Draw a stroke to the text only if a provider provides realtime but we don't use it.
      if (hasStroke) {
        ctx.save();
        ctx.textBaseline = 'middle';
        ctx.textAlign = 'center';
        ctx.font = `bold ${textSize + 2}px Arial`;
        ctx.strokeStyle = strokeColor;
        ctx.strokeText(text, origin, origin);
        ctx.restore();
      }

      // Draw a text
      ctx.textBaseline = 'middle';
      ctx.textAlign = 'center';
      ctx.fillStyle = fillColor;
      ctx.font = `bold ${textSize}px Arial`;
      ctx.strokeStyle = strokeColor;
      ctx.strokeText(text, origin, origin);
      ctx.fillText(text, origin, origin);

      cacheText[key] = canvas;
    }
  }
  return cacheText[key];
};

/** @private */
const cache = {};

/**
 * A tracker style that take in account the delay.
 *
 * @param {*} trajectory The trajectory to render.
 * @param {*} viewState The view state of the map.
 * @param {*} options Some options to change the rendering
 * @return a canvas
 */
const realtimeDefaultStyle = (trajectory, viewState, options) => {
  const {
    hoverVehicleId,
    selectedVehicleId,
    useDelayStyle,
    delayOutlineColor = '#000',
    delayDisplay = 300000,
    getRadius,
    getBgColor,
    getDelayColor,
    getDelayText,
    getTextColor,
    getTextSize,
  } = options;

  const { zoom, pixelRatio } = viewState;
  let { type } = trajectory.properties;
  const {
    train_id: id,
    line,
    delay,
    state,
    operator_provides_realtime_journey: operatorProvidesRealtime,
  } = trajectory.properties;
  let { name, text_color: textColor, color } = line || {};

  const cancelled = state === 'JOURNEY_CANCELLED';

  if (!type) {
    type = 'Rail';
  }

  if (!name) {
    name = 'I';
  }

  if (!textColor) {
    textColor = '#000000';
  }

  if (color && color[0] !== '#') {
    color = `#${color}`;
  }

  if (textColor[0] !== '#') {
    textColor = `#${textColor}`;
  }

  const z = Math.min(Math.floor(zoom || 1), 16);
  const hover = hoverVehicleId && hoverVehicleId === id;
  const selected = selectedVehicleId && selectedVehicleId === id;

  // Calcul the radius of the circle
  let radius = getRadius(type, z) * pixelRatio;
  const isDisplayStrokeAndDelay = radius >= 7 * pixelRatio;

  if (hover || selected) {
    radius = isDisplayStrokeAndDelay
      ? radius + 5 * pixelRatio
      : 14 * pixelRatio;
  }
  const mustDrawText = radius > 10 * pixelRatio;

  // Optimize the cache key, very important in high zoom level
  let key = `${radius}${hover}${selected}${cancelled}${delay}`;

  if (useDelayStyle) {
    key += `${operatorProvidesRealtime}`;
  } else {
    key += `${type}${color}`;
  }

  if (mustDrawText) {
    key += `${name}${textColor}`;
  }

  if (!cache[key]) {
    if (radius === 0) {
      cache[key] = null;
      return null;
    }

    const margin = 1 * pixelRatio;
    const radiusDelay = radius + 2;
    const markerSize = radius * 2;
    const size = radiusDelay * 2 + margin * 2 + 100 * pixelRatio; // add space for delay information
    const origin = size / 2;

    // Create the canvas
    const canvas = createCanvas(size, size);
    if (canvas) {
      const ctx = canvas.getContext('2d');

      if (isDisplayStrokeAndDelay && delay !== null) {
        // Draw circle delay background
        const delayBg = getDelayBgCanvas(
          origin,
          radiusDelay,
          getDelayColor(delay, cancelled),
        );
        ctx.drawImage(delayBg, 0, 0);
      }

      // Show delay if feature is hovered or if delay is above 5mins.
      if (
        isDisplayStrokeAndDelay &&
        (hover || delay >= delayDisplay || cancelled)
      ) {
        // Draw delay text
        const fontSize =
          Math.max(
            cancelled ? 19 : 14,
            Math.min(cancelled ? 19 : 17, radius * 1.2),
          ) * pixelRatio;
        const text = getDelayText(delay, cancelled);

        if (text) {
          const textWidth = text.length * fontSize;
          const delayText = getDelayTextCanvas(
            textWidth,
            text,
            fontSize,
            `bold ${fontSize}px arial, sans-serif`,
            getDelayColor(delay, cancelled, true),
            delayOutlineColor,
            pixelRatio,
          );
          ctx.drawImage(
            delayText,
            origin + radiusDelay + margin,
            origin - fontSize,
          );
        }
      }

      // Draw colored circle with black border
      let circleFillColor;
      if (useDelayStyle) {
        circleFillColor = getDelayColor(delay, cancelled);
      } else {
        circleFillColor = color || getBgColor(type);
      }

      const hasStroke = isDisplayStrokeAndDelay || hover || selected;

      const hasDash =
        isDisplayStrokeAndDelay &&
        useDelayStyle &&
        delay === null &&
        operatorProvidesRealtime === 'yes';

      const circle = getCircleCanvas(
        origin,
        radius,
        circleFillColor,
        hasStroke,
        hasDash,
        pixelRatio,
      );

      ctx.drawImage(circle, 0, 0);

      // Draw text in the circle
      if (mustDrawText) {
        const fontSize = Math.max(radius, 10);
        const textSize = getTextSize(ctx, markerSize, name, fontSize);
        const textColor2 = !useDelayStyle
          ? textColor || getTextColor(type)
          : '#000000';
        const hasStroke2 =
          useDelayStyle && delay === null && operatorProvidesRealtime === 'yes';

        const text = getTextCanvas(
          name,
          origin,
          textSize,
          textColor2,
          circleFillColor,
          hasStroke2,
          pixelRatio,
        );

        ctx.drawImage(text, 0, 0);
      }

      cache[key] = canvas;
    }
  }

  return cache[key];
};
export default realtimeDefaultStyle;

import realtimeDefaultStyle from './realtimeDefaultStyle';

/**
 * A tracker style that display the delay as backgroundColor.
 *
 * @param {*} trajectory The trajectory to render.
 * @param {*} viewState The view state of the map.
 * @param {*} options Some options to change the rendering
 * @return a canvas
 */
const realtimeDelayStyle = (trajectory, viewState, options) => {
  return realtimeDefaultStyle(trajectory, viewState, {
    ...options,
    useDelayStyle: true,
  });
};
export default realtimeDelayStyle;

/**
 * A very simple tracker style.
 * Display blue point for each train.
 */
let canvas;
const realtimeSimpleStyle = () => {
  if (!canvas) {
    canvas = document.createElement('canvas');
    canvas.width = 15;
    canvas.height = 15;
    const ctx = canvas.getContext('2d');
    ctx.arc(8, 8, 5, 0, 2 * Math.PI, false);
    ctx.fillStyle = '#8ED6FF';
    ctx.fill();
    ctx.lineWidth = 3;
    ctx.strokeStyle = 'black';
    ctx.stroke();
    ctx.lineWidth = 3;
  }
  return canvas;
};
export default realtimeSimpleStyle;

import mbt from './index';

if (typeof window !== 'undefined') {
  window.mbt = mbt;
}

export default mbt;

