    const rightTop = map.unproject({ x: width / pixelRatio, y: 0 }); // north east
    return [
        [leftTop.lng, leftTop.lat],
        [rightTop.lng, rightTop.lat],
        [rightBottom.lng, rightBottom.lat],
        [leftBottom.lng, leftBottom.lat],
