import { unByKey } from 'ol/Observable';

/**
 * Tracker. This class draw trajectories on a canvas.
 * @class
 * @param {Object} options
 * @private
 */
export default class Tracker {
  constructor(options) {
    const opts = {
      interpolate: true,
      ...options,
    };
    this.trajectories = [];
    this.interpolate = !!opts.interpolate;
    this.getPixelFromCoordinate = opts.getPixelFromCoordinate;
    this.hoverVehicleId = null;

    // we draw directly on the canvas since openlayers is too slow.
    this.canvas = opts.canvas || document.createElement('canvas');
    this.canvas.width = opts.width;
    this.canvas.height = opts.height;
    this.canvas.setAttribute(
      'style',
      [
        'position: absolute',
        'top: 0',
        'bottom: 0',
        'right: 0',
        'left: 0',
        'pointer-events: none',
        'visibility: visible',
        'margin-top: inherit', // for scrolling behavior.
      ].join(';'),
    );
    this.canvasContext = this.canvas.getContext('2d');
  }

  /**
   * Set visibility of the canvas.
   * @param {boolean} visible The visibility of the layer
   */
  setVisible(visible) {
    if (this.canvas) {
      this.canvas.style.visibility = visible ? 'visible' : 'hidden';
    }
  }

  /**
   * Define the trajectories.
   * @param {array<ol.feature>} trajectories
   */
  setTrajectories(trajectories = []) {
    if (this.sort) {
      trajectories.sort(this.sort);
    }

    this.trajectories = trajectories;
  }

  /**
   * Return the trajectories.
   * @returns {array<trajectory>} trajectories
   */
  getTrajectories() {
    return this.trajectories || [];
  }

  /**
   * Clear the canvas.
   * @private
   */
  clear() {
    if (this.canvasContext) {
      this.canvasContext.clearRect(0, 0, this.canvas.width, this.canvas.height);
    }
  }

  /**
   * Set the filter for tracker features.
   * @param {function} filter Filter function.
   */
  setFilter(filter) {
    this.filter = filter;
  }

  /**
   * Set the sort for tracker features.
   * @param {function} sort Sort function.
   */
  setSort(sort) {
    this.sort = sort;
  }

  /**
   * Set the id of the trajectory which is hovered.
   * @param {string} id Id of a vehicle.
   * @private
   */
  setHoverVehicleId(id) {
    if (id !== this.hoverVehicleId) {
      this.hoverVehicleId = id;
      this.renderTrajectories();
    }
  }

  /**
   * Set the tracker style.
   * @param {function} s OpenLayers style function.
   */
  setStyle(s) {
    this.style = s;
  }

  /**
   * Draw all the trajectories available to the canvas.
   * @param {Date} currTime
   * @private
   */
  renderTrajectories(currTime = Date.now(), size = [], resolution) {
    this.clear();
    const [width, height] = size;
    if (
      width &&
      height &&
      (this.canvas.width !== width || this.canvas.height !== height)
    ) {
      [this.canvas.width, this.canvas.height] = [width, height];
    }
    this.currResolution = resolution || this.currResolution;
    let hoverVehicleImg;
    let hoverVehiclePx;

    for (let i = (this.trajectories || []).length - 1; i >= 0; i -= 1) {
      const traj = this.trajectories[i];

      // We simplify the traj object
      const { geometry, timeIntervals, timeOffset } = traj;

      if (this.filter && !this.filter(traj)) {
        // eslint-disable-next-line no-continue
        continue;
      }

      let coord = null;
      let rotation;

      if (timeIntervals && timeIntervals.length > 1) {
        const now = currTime - (timeOffset || 0);
        let start;
        let end;
        let startFrac;
        let endFrac;
        let timeFrac;

        // Search th time interval.
        for (let j = 0; j < timeIntervals.length - 1; j += 1) {
          // Rotation only available in tralis layer.
          [start, startFrac, rotation] = timeIntervals[j];
          [end, endFrac] = timeIntervals[j + 1];

          if (start <= now && now <= end) {
            break;
          } else {
            start = null;
            end = null;
          }
        }

        if (start && end) {
          // interpolate position inside the time interval.
          timeFrac = this.interpolate
            ? Math.min((now - start) / (end - start), 1)
            : 0;

          const geomFrac = this.interpolate
            ? timeFrac * (endFrac - startFrac) + startFrac
            : 0;

          coord = geometry.getCoordinateAt(geomFrac);

          // We set the rotation and the timeFraction of the trajectory (used by tralis).
          this.trajectories[i].rotation = rotation;
          this.trajectories[i].endFraction = timeFrac;

          // It happens that the now date was some ms before the first timeIntervals we have.
        } else if (now < timeIntervals[0][0]) {
          [[, , rotation]] = timeIntervals;
          timeFrac = 0;
          coord = geometry.getFirstCoordinate();
        } else if (now > timeIntervals[timeIntervals.length - 1][0]) {
          [, , rotation] = timeIntervals[timeIntervals.length - 1];
          timeFrac = 1;
          coord = geometry.getLastCoordinate();
        }

        // We set the rotation and the timeFraction of the trajectory (used by tralis).
        this.trajectories[i].rotation = rotation || 0;
        this.trajectories[i].endFraction = timeFrac || 0;
      }

      if (coord) {
        // We set the rotation of the trajectory (used by tralis).
        this.trajectories[i].coordinate = coord;
        const px = this.getPixelFromCoordinate(coord);

        if (!px) {
          // eslint-disable-next-line no-continue
          continue;
        }

        const vehicleImg = this.style(traj, this.currResolution);
        if (this.hoverVehicleId !== traj.id) {
          this.canvasContext.drawImage(
            vehicleImg,
            px[0] - vehicleImg.height / 2,
            px[1] - vehicleImg.height / 2,
          );
        } else {
          // Store the canvas to draw it at the end
          hoverVehicleImg = vehicleImg;
          hoverVehiclePx = px;
        }
      }
    }
    if (hoverVehicleImg) {
      this.canvasContext.drawImage(
        hoverVehicleImg,
        hoverVehiclePx[0] - hoverVehicleImg.height / 2,
        hoverVehiclePx[1] - hoverVehicleImg.height / 2,
      );
    }
  }

  /**
   * Clean the canvas and the events the tracker.
   * @private
   */
  destroy() {
    unByKey(this.olEventsKeys);
    this.clear();
  }
}

import Observable from 'ol/Observable';
import { v4 as uuid } from 'uuid';

/**
 * A class representing a layer to display on map.
 *
 * @example
 *
 * const layer = new Layer({
 *   name: 'myLayer',
 * });
 *
 * @classproperty {string} name - Name of the layer
 * @classproperty {string} key - Identifier of the layer. Must be unique.
 * @classproperty {boolean} isBaseLayer - Define if the layer is a base layer. Read-only.
 * @classproperty {boolean} isQueryable - Define if the layer can be queried. Read-only.
 * @classproperty {boolean} isReactSpatialLayer - Custom property for duck typing since `instanceof` is not working when the instance was created on different bundles. Read-only.
 * @classproperty {Layer[]} children - List of children.
 * @classproperty {string} copyright - Copyright.
 * @classproperty {boolean} visible - Define if the layer is visible or not.
 * @classproperty {Object} properties - Custom properties.
 * @classproperty {ol/Map~Map|mapboxgl.Map} map - The map where the layer is displayed.
 */
export default class Layer extends Observable {
  /**
   * Constructor
   *
   * @param {Object} options
   * @param {string} [options.name=uuid()] Layer name. Default use a generated uuid.
   * @param {string} [options.key=uuid().toLowerCase()] Layer key, will use options.name.toLowerCase() if not specified.
   * @param {string} [options.copyright=undefined] Copyright-Statement.
   * @param {Array<Layer>} [options.children=[]] Sublayers.
   * @param {Object} [options.properties={}] Application-specific layer properties.
   * @param {boolean} [options.visible=true] If true this layer is visible on the map.
   * @param {boolean} [options.isBaseLayer=false] If true this layer is a baseLayer.
   * @param {boolean} [options.isQueryable=false] If true feature information can be queried by the react-spatial LayerService. Default is undefined, but resulting to true if not strictly set to false.
   */
  constructor(options) {
    super();
    this.defineProperties(options);

    // Add click callback
    const { onClick } = options;
    if (onClick) {
      this.onClick(onClick);
    }
  }

  /**
   * Define layer's properties.
   *
   * @ignore
   */
  defineProperties({
    name,
    key,
    children,
    copyright,
    visible,
    properties,
    isBaseLayer,
    isQueryable,
  }) {
    const uid = uuid();
    const dfltName = name || uid;
    Object.defineProperties(this, {
      name: {
        value: dfltName,
      },
      key: {
        value: key || dfltName.toLowerCase(),
      },
      isBaseLayer: {
        value: !!isBaseLayer,
      },
      isQueryable: {
        value: !!isQueryable,
      },
      // Custom property for duck typing since `instanceof` is not working
      // when the instance was created on different bundles.
      isReactSpatialLayer: {
        value: true,
      },
      children: {
        value: children || [],
        writable: true,
      },
      copyright: {
        get: () => {
          return this.get('copyright', copyright);
        },
        set: (newCopyright) => {
          return this.set('copyright', newCopyright);
        },
      },
      visible: {
        value: visible === undefined ? true : visible,
      },
      properties: {
        value: properties || {},
      },
      map: {
        writable: true,
      },
      /**
       * Callback function when a user click on a vehicle.
       */
      clickCallbacks: {
        value: [],
      },
    });
  }

  /**
   * Initialize the layer with the map passed in parameters.
   *
   * @param {ol/Map~Map|mapboxgl.Map} map A map.
   */
  init(map) {
    this.terminate();
    /** @ignore */
    this.map = map;
  }

  /**
   * Terminate what was initialized in init function. Remove layer, events...
   */
  // eslint-disable-next-line class-methods-use-this
  terminate() {}

  /**
   * Get a layer property.
   *
   * @param {string} name Property name.
   * @returns {property} Property
   */
  get(name) {
    return this.properties[name];
  }

  /**
   * Set a layer property.
   *
   * @param {string} name Property name.
   * @param {string} value Value.
   */
  set(name, value) {
    this.properties[name] = value;
    this.dispatchEvent({
      type: `change:${name}`,
      target: this,
    });
  }

  /**
   * Change the visibility of the layer
   *
   * @param {boolean} visible Defines the visibility of the layer
   * @param {boolean} [stopPropagationDown]
   * @param {boolean} [stopPropagationUp]
   * @param {boolean} [stopPropagationSiblings]
   */
  setVisible(
    visible,
    stopPropagationDown = false,
    stopPropagationUp = false,
    stopPropagationSiblings = false,
  ) {
    if (visible === this.visible) {
      return;
    }

    /** @ignore */
    this.visible = visible;

    this.dispatchEvent({
      type: 'change:visible',
      target: this,
      stopPropagationDown,
      stopPropagationUp,
      stopPropagationSiblings,
    });
  }

  /**
   * Returns an array with visible child layers
   *
   * @returns {Layer[]} Visible children
   */
  getVisibleChildren() {
    return this.children.filter((child) => child.visible);
  }

  /**
   * Checks whether the layer has child layers with visible set to True
   *
   * @returns {boolean} True if the layer has visible child layers
   */
  hasVisibleChildren() {
    return !!this.hasChildren(true);
  }

  /**
   * Checks whether the layer has any child layers with visible equal to the input parameter
   *
   * @param {boolean} visible The state to check the childlayers against
   * @returns {boolean} True if the layer has children with the given visibility
   */
  hasChildren(visible) {
    return !!this.children.find((child) => child.visible === visible);
  }

  /**
   * Add a child layer
   *
   * @param {Layer} layer Add a child layer
   */
  addChild(layer) {
    this.children.unshift(layer);
    this.dispatchEvent({
      type: `change:children`,
      target: this,
    });
  }

  /**
   * Removes a child layer by layer name
   *
   * @param {string} name Layer's name
   */
  removeChild(name) {
    for (let i = 0; i < this.children.length; i += 1) {
      if (this.children[i].name === name) {
        this.children.splice(i, 1);
        return;
      }
    }
    this.dispatchEvent({
      type: `change:children`,
      target: this,
    });
  }

  /**
   * Request feature information for a given coordinate.
   *
   * @param {number[2]} coordinate Coordinate to request the information at.
   * @returns {Promise<{layer:Layer, features:Object[], coordinate:number[]}>} Promise with features, layer and coordinate
   *  or null if no feature was hit.
   */
  getFeatureInfoAtCoordinate() {
    // This layer returns no feature info.
    // The function is implemented by inheriting layers.
    return Promise.resolve({
      layer: this,
      features: [],
      coordinate: null,
    });
  }

  /**
   * Listens to click events on the layer.
   * @param {function} callback Callback function, called with the clicked
   *   features,
   *   the layer instance and the click event.
   */
  onClick(callback) {
    if (typeof callback === 'function') {
      if (!this.clickCallbacks.includes(callback)) {
        this.clickCallbacks.push(callback);
      }
    } else {
      throw new Error('callback must be of type function.');
    }
  }

  /**
   * Unlistens to click events on the layer.
   * @param {function} callback Callback function, called with the clicked
   *   features,
   *   the layer instance and the click event.
   */
  unClick(callback) {
    if (typeof callback === 'function') {
      const idx = this.clickCallbacks.indexOf(callback);
      if (idx >= -1) {
        this.clickCallbacks.splice(idx, 1);
      }
    }
  }
}

/* eslint-disable class-methods-use-this */
/* eslint-disable max-classes-per-file */
import { buffer, containsCoordinate } from 'ol/extent';
import { unByKey } from 'ol/Observable';
import Tracker from '../Tracker';
import { timeSteps } from '../trackerConfig';

/**
 * TrackerLayerInterface.
 *
 * @classproperty {boolean} isTrackerLayer - Property for duck typing since `instanceof` is not working when the instance was created on different bundles.
 * @classproperty {boolean} isHoverActive - Activate/deactivate pointer hover effect.
 * @classproperty {function} style - Style of the vehicle.
 * @classproperty {FilterFunction} filter - Time speed.
 * @classproperty {function} sort - Set the filter for tracker features.
 */
export class TrackerLayerInterface {
  /**
   * Initalize the Tracker.
   * @param map
   * @param {Object} options
   * @param {number} [options.width] Canvas's width.
   * @param {number} [options.height] Canvas's height.
   * @param {function} [options.getPixelFromCoordinate] Convert an EPSG:3857 coordinate to a canvas pixel (origin top-left).
   */
  // eslint-disable-next-line no-unused-vars
  init(map, options) {}

  /**
   * Destroy the Tracker.
   */
  terminate() {}

  /**
   * Start the clock.
   *
   * @param {Array<number>} size Map's size: [width, height].
   * @param {number} zoom Map's zoom level.
   * @param {number} resolution Map's resolution.
   */
  // eslint-disable-next-line no-unused-vars
  start(size, zoom, resolution) {}

  // eslint-disable-next-line no-unused-vars
  startUpdateTime(zoom) {}

  /**
   * Stop the clock.
   */
  stop() {}

  stopUpdateTime() {}

  /**
   * Set the current time, it triggers a rendering of the trajectories.
   * @param {dateString | value} time
   */
  // eslint-disable-next-line no-unused-vars
  setCurrTime(time, size, resolution) {}

  /**
   * Get vehicle.
   * @param {function} filterFc A function use to filter results.
   */
  // eslint-disable-next-line no-unused-vars
  getVehicle(filterFc) {}

  /**
   * Returns the vehicle which are at the given coordinates.
   * Returns null when no vehicle is located at the given coordinates.
   * @param {ol.coordinate} coordinate
   * @returns {ol.feature | null} Vehicle feature
   */
  // eslint-disable-next-line no-unused-vars
  getVehiclesAtCoordinate(coordinate, resolution = 1) {}

  /**
   * Get the duration before the next update depending on zoom level.
   * @private
   */
  // eslint-disable-next-line no-unused-vars
  getRefreshTimeInMs(zoom) {}

  /**
   * Define a default style of the vehicle.s
   * Draw a blue circle with the id of the props parameter.
   *
   * @param {Object} props Properties
   * @private
   */
  // eslint-disable-next-line no-unused-vars
  defaultStyle(props) {}
}

/**
 * Mixin for TrackeLayerInterface.
 *
 * @param {Class} Base  A class to extend with {TrackerLayerInterface} functionnalities.
 * @return {Class}  A class that implements <TrackerLayerInterface> class and extends Base;
 */
const TrackerLayerMixin = (Base) =>
  class extends Base {
    defineProperties(options) {
      const { isHoverActive, style, speed } = {
        isHoverActive: true,
        ...options,
      };
      let cuurSpeed = speed || 1;
      super.defineProperties(options);
      Object.defineProperties(this, {
        isTrackerLayer: { value: true },
        isHoverActive: {
          value: !!isHoverActive,
          writable: true,
        },
        style: {
          value: style || this.defaultStyle,
        },
        speed: {
          get: () => cuurSpeed,
          set: (newSpeed) => {
            cuurSpeed = newSpeed;
            this.start();
          },
        },
        filter: {
          get: () => this.tracker.filter,
          set: (filter) => {
            if (this.tracker) {
              this.tracker.setFilter(filter);
            }
          },
        },
        sort: {
          get: () => this.tracker.sort,
          set: (sort) => {
            if (this.sort) {
              this.tracker.setSort(sort);
            }
          },
        },

        /**
         * The tracker that renders the trajectories.
         */
        tracker: { value: null, writable: true },

        /**
         * Canvas cache object for trajectories drawn.
         */
        styleCache: { value: {} },

        /**
         * Time used to display the trajectories.
         */
        currTime: {
          value: new Date(),
          writable: true,
        },

        /**
         * Keep track of the last time used to render trajectories.
         * Useful when the speed increase.
         */
        lastUpdateTime: {
          value: new Date(),
          writable: true,
        },
      });
    }

    /**
     * Initalize the Tracker.
     * @param map
     * @param {Object} options
     * @param {Number} [options.width] Canvas's width.
     * @param {Number} [options.height] Canvas's height.
     * @param {function} [options.getPixelFromCoordinate] Convert an EPSG:3857 coordinate to a canvas pixel (origin top-left).
     */
    init(map, options) {
      super.init(map);
      this.tracker = new Tracker(options);
      this.tracker.setStyle((props, r) => this.style(props, r));

      if (this.visible) {
        this.start();
      }

      this.visibilityRef = this.on('change:visible', (evt) => {
        if (evt.target.visible) {
          this.start();
        } else {
          this.stop();
        }
      });
    }

    /**
     * Destroy the Tracker.
     */
    terminate() {
      this.stop();
      unByKey(this.visibilityRef);
      if (this.tracker) {
        this.tracker.destroy();
        this.tracker = null;
      }
      super.terminate();
    }

    /**
     * Start the clock.
     *
     * @param {Array<Number>} size Map's size: [width, height].
     * @param {Number} zoom Map's zoom level.
     * @param {Number} resolution Map's resolution.
     */
    start(size, zoom, resolution) {
      this.stop();
      this.tracker.setVisible(true);
      this.tracker.renderTrajectories(this.currTime, size, resolution);
      this.startUpdateTime(zoom);
    }

    startUpdateTime(zoom) {
      this.updateTimeInterval = setInterval(() => {
        const newTime =
          this.currTime.getTime() +
          (new Date() - this.lastUpdateTime) * this.speed;
        this.setCurrTime(newTime);
      }, this.getRefreshTimeInMs(zoom));
    }

    /**
     * Stop the clock.
     */
    stop() {
      this.stopUpdateTime();
      if (this.tracker) {
        this.tracker.setVisible(false);
        this.tracker.clear();
      }
    }

    stopUpdateTime() {
      clearInterval(this.updateTimeInterval);
    }

    /**
     * Set the current time, it triggers a rendering of the trajectories.
     * @param {dateString | value} time
     */
    setCurrTime(time, size, resolution) {
      const newTime = new Date(time);
      this.currTime = newTime;
      this.lastUpdateTime = new Date();
      this.tracker.renderTrajectories(this.currTime, size, resolution);
    }

    /**
     * Get vehicle.
     * @param {function} filterFc A function use to filter results.
     */
    getVehicle(filterFc) {
      return this.tracker.getTrajectories().filter(filterFc);
    }

    /**
     * Returns an array of vehicles located at the given coordinates and resolution.
     *
     * @param {number[2]} coordinate
     * @param {number[2]} resolution
     * @returns {Object[]} Array of vehicle.
     */
    getVehiclesAtCoordinate(coordinate, resolution = 1) {
      const ext = buffer([...coordinate, ...coordinate], 10 * resolution);
      const trajectories = this.tracker.getTrajectories();
      const vehicles = [];
      for (let i = 0; i < trajectories.length; i += 1) {
        if (
          trajectories[i].coordinate &&
          containsCoordinate(ext, trajectories[i].coordinate)
        ) {
          vehicles.push(trajectories[i]);
        }
      }

      return vehicles;
    }

    /**
     * Get the duration before the next update depending on zoom level.
     * @private
     */
    getRefreshTimeInMs(zoom) {
      const roundedZoom = Math.round(zoom);
      const timeStep = timeSteps[roundedZoom] || 25;
      const nextTick = Math.max(25, timeStep / this.speed);
      return nextTick;
    }

    /**
     * Define a default style of the vehicle.s
     * Draw a blue circle with the id of the props parameter.
     *
     * @param {Object} props Properties
     * @private
     */
    defaultStyle(props) {
      const { id: text } = props;
      if (this.styleCache[text]) {
        return this.styleCache[text];
      }
      const canvas = document.createElement('canvas');
      canvas.width = 200;
      canvas.height = 15;
      const ctx = canvas.getContext('2d');
      ctx.arc(8, 8, 5, 0, 2 * Math.PI, false);
      ctx.fillStyle = '#8ED6FF';
      ctx.fill();
      ctx.lineWidth = 3;
      ctx.strokeStyle = 'black';
      ctx.stroke();
      ctx.font = 'bold 12px arial';
      ctx.strokeStyle = 'white';
      ctx.lineWidth = 3;
      ctx.strokeText(text, 20, 10);
      ctx.fillStyle = 'black';
      ctx.fillText(text, 20, 10);
      this.styleCache[text] = canvas;
      return this.styleCache[text];
    }
  };

export default TrackerLayerMixin;

