/**
 * @ignore
 */
const getTypeIndex = (type) => {
  if (typeof type === 'string') {
    const matched = types.find((t) => new RegExp(type).test(t));
    return types.indexOf(matched);
  }
  return type;
};

/**
 * @ignore
 */
export const getRadius = (type = 0, zoom) => {
  try {
    const typeIdx = getTypeIndex(type);
    return trackerRadiusMapping[typeIdx][zoom];
  } catch (e) {
    return 1;
  }
};

/**
 * @ignore
 */
export const getBgColor = (type = 0) => {
  try {
    const typeIdx = getTypeIndex(type);
    return bgColors[typeIdx];
  } catch (e) {
    return 1;
  }
};

/**
 * @ignore
 */
export const getTextColor = (type = 0) => {
  try {
    const typeIdx = getTypeIndex(type);
    return textColors[typeIdx];
  } catch (e) {
    return 1;
  }
};

/**
 * @ignore
 */
export const getTextSize = (ctx, markerSize, text, fontSize) => {
  ctx.font = `bold ${fontSize}px Arial`;
  let newText = ctx.measureText(text);

  const maxiter = 15;
  let i = 0;

  while (newText.width > markerSize - 6 && i < maxiter) {
    // eslint-disable-next-line no-param-reassign
    fontSize -= 0.5;
    ctx.font = `bold ${fontSize}px arial, sans-serif`;
    newText = ctx.measureText(text);
    i += 1;
  }
  return fontSize;
};

/**
 * @ignore
 */
export const getDelayColor = (delayInMs, cancelled) => {
  if (cancelled) {
    return '#ff0000';
  }
  if (delayInMs >= 3600000) {
    return '#ed004c'; // pink { r: 237, g: 0, b: 76, s: '237,0,76' };
  }
  if (delayInMs >= 500000) {
    return '#e80000'; // red { r: 232, g: 0, b: 0, s: '232,0,0' };
  }
  if (delayInMs >= 300000) {
    return '#ff4a00'; // orange { r: 255, g: 74, b: 0, s: '255,74,0' };
  }
  if (delayInMs >= 180000) {
    return '#f7bf00'; // yellow { r: 247, g: 191, b: 0, s: '247,191,0' };
  }
  if (delayInMs === null) {
    return '#a0a0a0'; // grey { r: 160, g: 160, b: 160, s: '160,160,160' };
  }
  return '#00a00c'; // green { r: 0, g: 160, b: 12, s: '0,160,12' };
};

/**
 * @ignore
 */
export const getDelayText = (delayInMs, cancelled) => {
  if (cancelled) {
    return String.fromCharCode(10006);
  }
  if (delayInMs > 3600000) {
    const rounded = Math.round(delayInMs / 3600000);
    return `+${rounded}h`;
  }

  if (delayInMs > 59000) {
    const rounded = Math.round(delayInMs / 60000);
    return `+${rounded}m`;
  }

  if (delayInMs > 0) {
    return `+${delayInMs}s`;
  }

  return '';
};

import Map from './ol/Map';
import {
  Layer as olLayer,
  WMSLayer as olWMSLayer,
  MapboxLayer as olMapboxLayer,
  TrajservLayer as olTrajservLayer,
} from './ol';
import { TrajservLayer as mbTrajservLayer } from './mapbox';

const exports = {
  mapbox: {
    TrajservLayer: mbTrajservLayer,
  },
  ol: {
    Map,
    Layer: olLayer,
    WMSLayer: olWMSLayer,
    MapboxLayer: olMapboxLayer,
    TrajservLayer: olTrajservLayer,
  },
};

export default exports;

import { toLonLat, fromLonLat } from 'ol/proj';
import Layer from '../../common/layers/Layer';
import mixin from '../../common/mixins/TrackerLayerMixin';
import { getResolution } from '../utils';

/**
 * Responsible for loading tracker data.
 *
 * @extends {Layer}
 * @implements {TrackerLayerInterface}
 */
class TrackerLayer extends mixin(Layer) {
  constructor(options = {}) {
    super({
      ...options,
    });

    /** @ignores */
    this.onMapZoomEnd = this.onMapZoomEnd.bind(this);
    /** @ignores */
    this.onMapMouseMove = this.onMapMouseMove.bind(this);
  }

  /**
   * Initialize the layer.
   *
   * @param {mapboxgl.Map} map A [mapbox Map](https://docs.mapbox.com/mapbox-gl-js/api/map/).
   * @override
   */
  init(map) {
    if (!map) {
      return;
    }
    const { width, height } = map.getCanvas();

    super.init(map, {
      width,
      height,
      getPixelFromCoordinate: (coord) => {
        const pixelRatio = window.devicePixelRatio || 1;
        const [lng, lat] = toLonLat(coord);
        const { x, y } = this.map.project({ lng, lat });
        return [x * pixelRatio, y * pixelRatio];
      },
    });
  }

  /**
   * Set the current time, it triggers a rendering of the trajectories.
   *
   * @param {Date} time  The current time.
   */
  setCurrTime(time) {
    const canvas = this.map.getCanvas();
    super.setCurrTime(
      time,
      [canvas.width, canvas.height],
      getResolution(this.map),
      !this.map.isMoving() && !this.map.isRotating() && !this.map.isZooming(),
    );
  }

  /**
   * Start updating vehicles position.
   *
   * @listens {mapboxgl.map.event:zoomend} Listen to zoom end event, see {@link TrackerLayer#onMapZoomEnd}.
   * @listens {mapboxgl.map.event:mousemove} Listen to mousemove end, see {@link TrackerLayer#onMapMouseMove}.
   * @param {mapboxgl.Map} map A [mapbox Map](https://docs.mapbox.com/mapbox-gl-js/api/map/).
   * @override
   */
  start() {
    const canvas = this.map.getCanvas();
    super.start(
      [canvas.width, canvas.height],
      this.map.getZoom(),
      getResolution(this.map),
    );

    this.map.on('zoomend', this.onMapZoomEnd);

    if (this.isHoverActive) {
      this.map.on('mousemove', this.onMapMouseMove);
    }
  }

  /**
   * Stop updating vehicles position, and unlisten events.
   *
   * @override
   */
  stop() {
    super.stop();
    if (this.map) {
      this.map.off('zoomend', this.onMapZoomEnd);
      this.map.off('mousemove', this.onMapMouseMove);
    }
  }

  /**
   * Returns an array of vehicles located at the given coordinate.
   *
   * @param {number[2]} coordinate
   * @returns {Object[]} Array of vehicle.
   * @override
   */
  getVehiclesAtCoordinate(coordinate) {
    const resolution = getResolution(this.map);
    return super.getVehiclesAtCoordinate(coordinate, resolution);
  }

  /**
   * On zoomend we adjust the time interval of the update of vehicles positions.
   *
   * @private
   */
  onMapZoomEnd() {
    this.startUpdateTime(this.map.getZoom());
  }

  /**
   * On mousemove, we detect if a vehicle is heovered then updates the cursor's style.
   *
   * @param {mapboxgl.mapmouseevent} evt Map's mousemove event.
   * @private
   */
  onMapMouseMove(evt) {
    if (
      this.map.isMoving() ||
      this.map.isRotating() ||
      this.map.isZooming() ||
      !this.isHoverActive
    ) {
      this.map.getContainer().style.cursor = 'auto';
      return;
    }
    const [vehicle] = this.getVehiclesAtCoordinate(
      fromLonLat([evt.lngLat.lng, evt.lngLat.lat]),
    );
    this.map.getContainer().style.cursor = vehicle ? 'pointer' : 'auto';
    this.tracker.setHoverVehicleId(vehicle && vehicle.id);
  }
}

