import OLMap from 'ol/Map';
import OLLayer from 'ol/layer/Layer';
import Layer from './layers/Layer';

/**
 * An OpenLayers for handling {@link Layer|Layers}.
 * This class extends the OpenLayers class
 * {@link https://openlayers.org/en/latest/apidoc/module-ol_Map-Map.html|ol/Map}.
 *
 * @example
 * import { Map } from 'mobility-toolbox-js/src/ol';
 *
 * @class
 * @namespace
 * @param {Object} options Map options.
 * @param {Array.Layer} [options.layers] List of {@link Layer|Layers}.
 */
class Map extends OLMap {
  constructor(options = {}) {
    super({
      ...options,
      layers: (options.layers || []).map((l) =>
        l instanceof OLLayer ? l : l.olLayer,
      ),
    });

    this.mobilityLayers =
      (options.layers || []).filter((l) => l instanceof Layer) || [];
    this.mobilityLayers.forEach((l) => l.init(this));
  }

  /**
   * Adds a {@link Layer} to the map.
   * @param {Layer} The {@link Layer} to add.
   */
  addLayer(layer) {
    if (!layer.init) {
      super.addLayer(layer);
    } else {
      layer.init(this);
    }

    if (layer.olLayer) {
      super.addLayer(layer.olLayer);
    }

    if (layer instanceof Layer) {
      this.mobilityLayers.push(layer);
    }
  }

  /**
   * Returns a list of mobility layers.
   * @returns {Layer} {@link Layer}.
   */
  getMobilityLayers() {
    return this.mobilityLayers;
  }

  /**
   * Removes a given layer from the map.
   * @param {Layer} The {@link Layer} to remove.
   * @returns The removed layer (or undefined if the layer was not found).
   */
  removeLayer(layer) {
    if (layer instanceof Layer) {
      layer.terminate();
      this.mobilityLayers = this.mobilityLayers.filter((l) => l !== layer);
    }
    if (layer.olLayer) {
      super.removeLayer(layer);
    }
  }
}

export default Map;

export { default as Map } from './Map';
export { default as Layer } from './layers/Layer';
export { default as WMSLayer } from './layers/WMSLayer';
export { default as MapboxLayer } from './layers/MapboxLayer';
export { default as MapboxStyleLayer } from './layers/MapboxStyleLayer';
export { default as TrajservLayer } from './layers/TrajservLayer';
export { default as TrackerLayer } from './layers/TrackerLayer';

import { unByKey } from 'ol/Observable';
import LayerCommon from '../../common/layers/Layer';

/**
 * A class representing a layer to display on an OpenLayers map.
 *
 * @param {Object} options
 * @param {ol/Layer} options.olLayer The {@link https://openlayers.org/en/latest/apidoc/module-ol_layer_Layer-Layer.html ol/Layer} (required).
 * @param {string} [options.name=uuid()] Layer name. Default use a generated uuid.
 * @param {string} [options.key=uuid().toLowerCase()] Layer key, will use options.name.toLowerCase() if not specified.
 * @param {string} [options.copyright=undefined] Copyright-Statement.
 * @param {Array<Layer>} [options.children=[]] Sublayers.
 * @param {Object} [options.properties={}] Application-specific layer properties.
 * @param {boolean} [options.visible=true] If true this layer is the currently visible layer on the map.
 * @param {boolean} [options.isBaseLayer=false] If true this layer is a baseLayer.
 * @param {boolean} [options.isQueryable=false] If true feature information can be queried by the react-spatial LayerService. Default is undefined, but resulting to true if not strictly set to false.
 * @extends LayerCommon
 */
class Layer extends LayerCommon {
  constructor(options) {
    super(options);
    if (this.olLayer) {
      this.olLayer.setVisible(this.visible);
    }
  }

  defineProperties(options) {
    super.defineProperties(options);
    Object.defineProperties(this, {
      olLayer: { value: options.olLayer, writable: true },
      olListenersKeys: {
        value: [],
      },
    });
  }

  /**
   * Initialize the layer and listen to feature clicks.
   * @param {ol/Map~Map} map {@link https://openlayers.org/en/latest/apidoc/module-ol_Map-Map.html ol/Map}
   */
  init(map) {
    super.init(map);
  }

  /**
   * Terminate what was initialized in init function. Remove layer, events...
   */
  terminate() {
    unByKey(this.olListenersKeys);
    super.terminate();
  }

  /**
   * Change the visibility of the layer
   *
   * @param {boolean} visible Defines the visibility of the layer
   * @param {boolean} [stopPropagationDown]
   * @param {boolean} [stopPropagationUp]
   * @param {boolean} [stopPropagationSiblings]
   */
  setVisible(
    visible,
    stopPropagationDown = false,
    stopPropagationUp = false,
    stopPropagationSiblings = false,
  ) {
    if (visible === this.visible) {
      return;
    }
    if (this.olLayer) {
      this.olLayer.setVisible(this.visible);
    }

    super.setVisible(
      visible,
      stopPropagationDown,
      stopPropagationUp,
      stopPropagationSiblings,
    );
  }
}
export default Layer;

/* eslint-disable no-underscore-dangle */
import { toLonLat } from 'ol/proj';
import { unByKey } from 'ol/Observable';
import mapboxgl from 'mapbox-gl';
import OLLayer from 'ol/layer/Layer';
import GeoJSON from 'ol/format/GeoJSON';
import Layer from './Layer';

const getCopyrightFromSources = (mbMap) => {
  let copyrights = [];
  const regex = /<[^>]*>[^>]*<\/[^>]*>/g;
  // Trick from Mapbox AttributionControl to know if the source is used.
  const { sourceCaches } = mbMap.style;
  Object.entries(sourceCaches).forEach(([, sourceCache]) => {
    if (sourceCache.used) {
      copyrights = copyrights.concat(
        regex.exec(sourceCache.getSource().attribution),
      );
    }
  });
  return Array.from(
    new Set(copyrights.filter((copyright) => !!copyright)),
  ).join(', ');
};

/**
 * A class representing Mapboxlayer to display on BasicMap
 * @class
 * @inheritDoc
 * @param {Object} [options]
 */
export default class MapboxLayer extends Layer {
  constructor(options = {}) {
    const olLayer = new OLLayer({
      render: (frameState) => {
        let changed = false;
        const canvas = this.mbMap.getCanvas();
        const { viewState } = frameState;

        const { visible } = this.olLayer;
        if (this.renderState.visible !== visible) {
          canvas.style.display = visible ? 'block' : 'none';
          this.renderState.visible = visible;
        }

        // Needed since mapbox-gl 1.9.0.
        // Without you don't see others ol layers on top.
        canvas.style.position = 'absolute';

        const opacity = this.olLayer.getOpacity();
        if (this.renderState.opacity !== opacity) {
          canvas.style.opacity = opacity;
          this.renderState.opacity = opacity;
        }

        // adjust view parameters in mapbox
        const { rotation } = viewState;
        if (rotation && this.renderState.rotation !== rotation) {
          this.mbMap.rotateTo((-rotation * 180) / Math.PI, {
            animate: false,
          });
          changed = true;
          this.renderState.rotation = rotation;
        }

        if (
          this.renderState.zoom !== viewState.zoom ||
          this.renderState.center[0] !== viewState.center[0] ||
          this.renderState.center[1] !== viewState.center[1]
        ) {
          this.mbMap.jumpTo({
            center: toLonLat(viewState.center),
            zoom: viewState.zoom - 1,
            animate: false,
          });
          changed = true;
          this.renderState.zoom = viewState.zoom;
          this.renderState.center = viewState.center;
        }

        // cancel the scheduled update & trigger synchronous redraw
        // see https://github.com/mapbox/mapbox-gl-js/issues/7893#issue-408992184
        // NOTE: THIS MIGHT BREAK WHEN UPDATING MAPBOX
        if (this.mbMap && this.mbMap.style && this.mbMap.isStyleLoaded()) {
          if (this.mbMap._frame) {
            this.mbMap._frame.cancel();
            this.mbMap._frame = null;
          }
          try {
            if (changed) {
              this.mbMap._render();
            }
          } catch (err) {
            // ignore render errors because it's probably related to
            // a render during an update of the style.
            // eslint-disable-next-line no-console
            console.warn(err);
          }
        }

        return canvas;
      },
    });

    super({
      ...options,
      olLayer,
    });
    this.options = options;
    this.styleUrl = options.url;
  }

  /**
   * @inheritdoc
   * @param {ol/Map~Map} map {@link https://openlayers.org/en/latest/apidoc/module-ol_Map-Map.html ol/Map}
   */
  init(map) {
    super.init(map);

    // Options the last render run did happen. If something changes
    // we have to render again
    this.renderState = {
      center: [0, 0],
      zoom: undefined,
      rotation: undefined,
      visible: undefined,
      opacity: undefined,
    };

    if (!this.map || !this.map.getTargetElement() || this.mbMap) {
      return;
    }

    this.format = new GeoJSON({
      featureProjection: this.map.getView().getProjection(),
    });

    // If the map hasn't been resized, the center could be [NaN,NaN].
    // We set default good value for the mapbox map, to avoid the app crashes.
    let [x, y] = map.getView().getCenter();
    if (!x || !y) {
      x = 0;
      y = 0;
    }

    this.mbMap = new mapboxgl.Map({
      style: this.styleUrl,
      attributionControl: false,
      boxZoom: false,
      center: toLonLat([x, y]),
      container: this.map.getTargetElement(),
      doubleClickZoom: false,
      dragPan: false,
      dragRotate: false,
      interactive: false,
      keyboard: false,
      pitchWithRotate: false,
      scrollZoom: false,
      touchZoomRotate: false,
      fadeDuration:
        'fadeDuration' in this.options ? this.options.fadeDuration : 300,
      // Needs to be true to able to export the canvas, but could lead to performance issue on mobile.
      preserveDrawingBuffer: this.options.preserveDrawingBuffer || false,
    });

    this.mbMap.once('load', () => {
      this.loaded = true;
      if (!this.copyright) {
        this.copyright = getCopyrightFromSources(this.mbMap);
      }
      this.dispatchEvent({
        type: 'load',
        target: this,
      });
    });

    this.changeSizeRef = this.map.on('change:size', () => {
      try {
        this.mbMap.resize();
      } catch (err) {
        // ignore render errors
        // eslint-disable-next-line no-console
        console.warn(err);
      }
    });

    const mapboxCanvas = this.mbMap.getCanvas();
    if (mapboxCanvas) {
      // Set default tabIndex to -1, so we can't access the canvas via Tab nav.
      mapboxCanvas.setAttribute('tabindex', this.options.tabIndex || -1);
    }
  }

  /**
   * Request feature information for a given coordinate.
   * @param {ol.Coordinate} coordinate Coordinate to request the information at.
   * @param {Object} options A mapbox {@link https://openlayers.org/en/latest/apidoc/module-ol_layer_Layer-Layer.html options object}.
   * @returns {Promise<Object>} Promise with features, layer and coordinate
   *  or null if no feature was hit.
   */
  getFeatureInfoAtCoordinate(coordinate, options) {
    // Ignore the getFeatureInfo until the mapbox map is loaded
    if (
      !options ||
      !this.format ||
      !this.mbMap ||
      !this.mbMap.isStyleLoaded()
    ) {
      return Promise.resolve({ coordinate, features: [], layer: this });
    }

    const pixel = coordinate && this.mbMap.project(toLonLat(coordinate));
    // At this point we get GeoJSON Mapbox feature, we transform it to an Openlayers
    // feature to be consistent with other layers.
    const features = this.mbMap
      .queryRenderedFeatures(pixel, options)
      .map((feature) => this.format.readFeature(feature));

    return Promise.resolve({
      layer: this,
      features,
      coordinate,
    });
  }

  /**
   * Terminate what was initialized in init function. Remove layer, events...
   */
  terminate() {
    if (this.changeSizeRef) {
      unByKey(this.changeSizeRef);
    }
    if (this.mbMap) {
      // Some asynchrone repaints are triggered even if the mbMap has been removed,
      // to avoid display of errors we set an empty function.
      this.mbMap.triggerRepaint = () => {};
      this.mbMap.remove();
      this.mbMap = null;
    }
    this.loaded = false;
    super.terminate();
  }

  /**
   * Create exact copy of the MapboxLayer
   * @returns {MapboxLayer} MapboxLayer
   */
  clone() {
    return new MapboxLayer({ ...this.options, url: this.styleUrl });
  }
}

/* eslint-disable no-param-reassign */
import Layer from './Layer';

/**
 * Apply visibility to style layers that fits the filter function.
 * @private
 */
const applyLayoutVisibility = (mbMap, visible, filterFunc) => {
  const style = mbMap.getStyle();

  if (!mbMap || !style) {
    return;
  }

  if (filterFunc) {
    const visibilityValue = visible ? 'visible' : 'none';
    for (let i = 0; i < style.layers.length; i += 1) {
      const styleLayer = style.layers[i];
      if (filterFunc(styleLayer)) {
        if (mbMap.getLayer(styleLayer.id)) {
          mbMap.setLayoutProperty(styleLayer.id, 'visibility', visibilityValue);
        }
      }
    }
  }
};

/**
 * Layer for visualizing a specific set of layer from a MapboxLayer.
 * @class
 *
 * @example
 * import { MapboxStyleLayer } from 'mobility-toolbox-js/src/ol';
 *
 * @param {Object} [options] Layer options.
 * @inheritdoc
 */
class MapboxStyleLayer extends Layer {
  constructor(options = {}) {
    super(options);
    this.options = options;
    this.mapboxLayer = options.mapboxLayer;
    this.styleLayersFilter = options.styleLayersFilter;
    this.featureInfoFilter = options.featureInfoFilter || ((obj) => obj);
    this.queryRenderedLayersFilter = options.queryRenderedLayersFilter;
    this.highlightedFeatures = [];
    this.selectedFeatures = [];
    this.styleLayers =
      (options.styleLayer ? [options.styleLayer] : options.styleLayers) || [];
    this.addStyleLayers = this.addStyleLayers.bind(this);
    this.onLoad = this.onLoad.bind(this);
    if (options.filters) {
      this.addDynamicFilters = () => {
        this.setFilter(
          typeof options.filters === 'function'
            ? options.filters(this)
            : options.filters,
        );
      };
    }

    if (!this.styleLayersFilter && this.styleLayers) {
      const ids = this.styleLayers.map((s) => s.id);
      this.styleLayersFilter = (styleLayer) => {
        return ids.includes(styleLayer.id);
      };
    }
  }

  init(map) {
    if (!this.mapboxLayer.map) {
      this.mapboxLayer.init(map);
      this.olLayer = this.mapboxLayer.olLayer;
    }
    super.init(map);

    if (!this.map) {
      return;
    }

    // Apply the initial visibiltity.
    const { mbMap } = this.mapboxLayer;
    if (!mbMap) {
      // If the mbMap is not yet created because the  map has no target yet, we
      // relaunch the initialisation when it's the case.
      this.olListenersKeys.push(
        this.map.on('change:target', () => {
          this.init(map);
        }),
      );

      return;
    }

    // mbMap.loaded() and mbMap.isStyleLoaded() are reliable only on the first call of init.
    // On the next call (when a topic change for example), these functions returns false because
    // the style is being modified.
    // That's why we rely on a property instead for the next calls.
    if (this.mapboxLayer.loaded || mbMap.isStyleLoaded() || mbMap.loaded()) {
      this.onLoad();
    } else {
      mbMap.once('load', this.onLoad);
    }

    // Apply the visibiltity when layer's visibility change.
    this.olListenersKeys.push(
      this.on('change:visible', ({ target: layer }) => {
        if (this.isMbMapLoaded) {
          // Once the map is loaded we can apply vsiiblity without waiting
          // the style. Mapbox take care of the application of style changes.
          applyLayoutVisibility(mbMap, layer.visible, this.styleLayersFilter);
        }
      }),
    );

    this.olListenersKeys.push(
      this.mapboxLayer.on('change:styleurl', () => {
        this.addStyleLayers();
        if (this.addDynamicFilters) {
          this.addDynamicFilters();
        }
      }),
      // this.addDynamicFilters &&
      //   this.map.on('moveend', () => {
      //     this.addDynamicFilters();
      //   }),
    );
  }

  terminate(map) {
    const { mbMap } = this.mapboxLayer;
    if (!mbMap) {
      return;
    }

    mbMap.off('load', this.onLoad);
    if (this.isMbMapLoaded) {
      this.removeStyleLayers();
    }
    super.terminate(map);
  }

  addStyleLayers() {
    const { mbMap } = this.mapboxLayer;
    this.styleLayers.forEach((styleLayer) => {
      const { id, source } = styleLayer;
      if (mbMap.getSource(source) && !mbMap.getLayer(id)) {
        mbMap.addLayer(styleLayer);
      }
    });
    applyLayoutVisibility(mbMap, this.visible, this.styleLayersFilter);
  }

  removeStyleLayers() {
    const { mbMap } = this.mapboxLayer;
    this.styleLayers.forEach((styleLayer) => {
      if (mbMap.getLayer(styleLayer.id)) {
        mbMap.removeLayer(styleLayer.id);
      }
    });
  }

  onLoad() {
    this.isMbMapLoaded = true;
    this.addStyleLayers();

    if (this.addDynamicFilters) {
      this.addDynamicFilters();
    }
  }

  /**
   * Request feature information for a given coordinate.
   * @param {ol.Coordinate} coordinate Coordinate to request the information at.
   * @returns {Promise<Object>} Promise with features, layer and coordinate
   *  or null if no feature was hit.
   */
  getFeatureInfoAtCoordinate(coordinate) {
    const { mbMap } = this.mapboxLayer;
    // Ignore the getFeatureInfo until the mapbox map is loaded
    if (!mbMap || !mbMap.isStyleLoaded()) {
      return Promise.resolve({ coordinate, features: [], layer: this });
    }
    return this.mapboxLayer
      .getFeatureInfoAtCoordinate(coordinate, {
        layers: (this.queryRenderedLayersFilter
          ? mbMap.getStyle().layers.filter(this.queryRenderedLayersFilter)
          : this.styleLayers
        ).map((s) => s && s.id),
        validate: false,
      })
      .then((featureInfo) => {
        const features = featureInfo.features.filter((feature) => {
          return this.featureInfoFilter(
            feature,
            this.map.getView().getResolution(),
          );
        });
        this.highlight(features);
        return { ...featureInfo, features, layer: this };
      });
  }

  getFeatures2(resolve) {
    resolve(this.getFeatures());
  }

  setFilter(filter) {
    const { mbMap } = this.mapboxLayer;
    this.styleLayers.forEach(({ id }) => {
      if (mbMap.getLayer(id)) {
        mbMap.setFilter(id, filter);
      }
    });
  }

  setHoverState(features = [], state) {
    const options = this.styleLayers[0];
    features.forEach((feature) => {
      if ((!options.source && !options['source-layer']) || !feature.getId()) {
        if (!feature.getId()) {
          // eslint-disable-next-line no-console
          console.warn(
            "No feature's id found. To use the feature state functionnality, tiles must be generated with --generate-ids. See https://github.com/mapbox/tippecanoe#adding-calculated-attributes.",
            feature.getId(),
            feature.getProperties(),
          );
        }
        return;
      }

      this.mapboxLayer.mbMap.setFeatureState(
        {
          id: feature.getId(),
          source: options.source,
          sourceLayer: options['source-layer'],
        },
        { hover: state },
      );
    });
  }

  select(features = []) {
    this.setHoverState(this.selectedFeatures, false);
    this.selectedFeatures = features;
    this.setHoverState(this.selectedFeatures, true);
  }

  highlight(features = []) {
    // Filter out selected features
    const filtered = this.highlightedFeatures.filter((feature) => {
      return !this.selectedFeatures
        .map((feat) => feat.getId())
        .includes(feature.getId());
    });

    // Remove previous highlight
    this.setHoverState(filtered, false);
    this.highlightedFeatures = features;

    // Add highlight
    this.setHoverState(this.highlightedFeatures, true);
  }

  /**
   * Create exact copy of the MapboxLayer
   * @returns {MapboxLayer} MapboxLayer
   */
  clone(mapboxLayer) {
    const options = { ...this.options, mapboxLayer };
    return new MapboxStyleLayer(options);
  }
}

export default MapboxStyleLayer;

import OLLayer from 'ol/layer/Layer';
import { unByKey } from 'ol/Observable';
import mixin from '../../common/mixins/TrackerLayerMixin';
import Layer from './Layer';

/**
 * Responsible for loading tracker data.
 * @param {Object} options
 * @param {boolean} options.useDelayStyle Set the delay style.
 * @class
 * @extends {Layer}
 * @implements {TrackerLayerInterface}
 */
class TrackerLayer extends mixin(Layer) {
  constructor(options = {}) {
    const olLayer = new OLLayer({
      render: (frameState) => {
        if (this.tracker && this.tracker.canvas) {
          this.tracker.renderTrajectories(
            this.currTime,
            frameState.size,
            frameState.viewState.resolution,
          );
          return this.tracker.canvas;
        }
        return null;
      },
    });
    super({
      olLayer,
      ...options,
    });

    // Array of ol events key. Be careful to not override this value in child classe.
    this.olEventsKeys = [];
  }

  /**
   * Initialize the layer and listen to feature clicks.
   * @param {ol/Map~Map} map {@link https://openlayers.org/en/latest/apidoc/module-ol_Map-Map.html Map}
   * @private
   */
  init(map) {
    if (!map) {
      return;
    }

    super.init(map, {
      getPixelFromCoordinate: map.getPixelFromCoordinate.bind(map),
    });
  }

  /**
   * Set the current time, it triggers a rendering of the trajectories.
   * @param {dateString | value} time
   */
  setCurrTime(time) {
    const view = this.map.getView();
    super.setCurrTime(time, this.map.getSize(), view.getResolution());
  }

  /**
   * Trackerlayer is started
   * @param {ol/Map~Map} map {@link https://openlayers.org/en/latest/apidoc/module-ol_Map-Map.html ol/Map}
   * @private
   */
  start() {
    super.start(
      this.map.getSize(),
      this.currentZoom,
      this.map.getView().getResolution(),
    );

    this.olEventsKeys = [
      this.map.on('moveend', () => {
        const z = this.map.getView().getZoom();

        if (z !== this.currentZoom) {
          this.currentZoom = z;
          this.startUpdateTime(z);
        }
      }),
      this.map.on('pointermove', (evt) => {
        if (this.map.getView().getInteracting() || !this.isHoverActive) {
          return;
        }
        const [vehicle] = this.getVehiclesAtCoordinate(evt.coordinate);
        this.map.getTargetElement().style.cursor = vehicle ? 'pointer' : 'auto';
        this.tracker.setHoverVehicleId(vehicle && vehicle.id);
      }),
    ];
  }

  /**
   * Stop current layer,.
   * @private
   */
  stop() {
    super.stop();
    unByKey(this.olEventsKeys);
    this.olEventsKeys = [];
  }

  /**
   * Returns the vehicle which are at the given coordinates.
   * Returns null when no vehicle is located at the given coordinates.
   * @param {ol.coordinate} coordinate
   * @returns {ol.feature | null} Vehicle feature
   * @private
   */
  getVehiclesAtCoordinate(coordinate) {
    const resolution = this.map.getView().getResolution();
    return super.getVehiclesAtCoordinate(coordinate, resolution);
  }
}

export default TrackerLayer;

import Feature from 'ol/Feature';
import { transform as transformCoords } from 'ol/proj';
import { buffer, getWidth } from 'ol/extent';
import { Point, MultiPoint, LineString } from 'ol/geom';
import { Style, Fill, Stroke, Circle } from 'ol/style';
import { Vector as VectorLayer } from 'ol/layer';
import { Vector as VectorSource } from 'ol/source';
import TrackerLayer from './TrackerLayer';
import { getUTCTimeString } from '../../common/timeUtils';
import { getBgColor } from '../../common/trackerConfig';
import mixin from '../../common/mixins/TrajservLayerMixin';

/**
 * Responsible for loading and display data from a Trajserv service.
 *
 * @example
 * import { TrajservLayer } from 'mobility-toolbox-js/src/mapbox';
 *
 * const layer = new TrajservLayer({
 *   url: 'https://api.geops.io/tracker/v1',
 *   apiKey: [yourApiKey],
 * });
 *
 *
 * @see <a href="/api/class/src/api/trajserv/TrajservAPI%20js~TrajservAPI%20html">TrajservAPI</a>
 * @see <a href="/examples/ol-tracker">OL tracker example</a>
 *
 * @extends {TrackerLayer}
 * @implements {TrajservLayerInterface}
 */
class TrajservLayer extends mixin(TrackerLayer) {
  constructor(options) {
    super(options);
    Object.defineProperties(this, {
      vectorLayer: {
        value: new VectorLayer({
          source: new VectorSource({ features: [] }),
        }),
      },
    });
  }

  init(map) {
    if (!map) {
      return;
    }

    map.addLayer(this.vectorLayer);
    super.init(map);
  }

  terminate() {
    if (this.map) {
      this.map.removeLayer(this.vectorLayer);
    }
    super.terminate();
  }

  start() {
    if (!this.map) {
      return;
    }
    super.start();
    this.olEventsKeys = [
      ...this.olEventsKeys,
      this.map.on('singleclick', this.onMapClick.bind(this)),
      this.map.on('moveend', this.onMoveEnd.bind(this)),
    ];
  }

  /**
   * Callback on 'moveend' event.
   * @private
   */
  onMoveEnd() {
    this.updateTrajectories();
    if (this.selectedVehicleId && this.journeyId) {
      this.highlightTrajectory();
    }
  }

  /**
   * Callback on 'singleclick' event.
   * @private
   */
  onMapClick(evt) {
    if (!this.clickCallbacks.length) {
      return;
    }

    const [vehicle] = this.getVehiclesAtCoordinate(evt.coordinate);
    const features = [];

    if (vehicle) {
      const geom = vehicle.coordinate ? new Point(vehicle.coordinate) : null;
      features.push(new Feature({ geometry: geom, ...vehicle }));

      if (features.length) {
        this.selectedVehicleId = features[0].get('id');
        this.journeyId = features[0].get('journeyIdentifier');
        this.updateTrajectoryStations(this.selectedVehicleId).then(
          (trajStations) => {
            this.clickCallbacks.forEach((callback) =>
              callback(trajStations, this, evt),
            );
          },
        );
      }
    } else {
      this.selectedVehicleId = null;
      this.vectorLayer.getSource().clear();
      this.clickCallbacks.forEach((callback) => callback(null, this, evt));
    }
  }

  /**
   * Draw the trajectory as a line with points for each stop.
   * @param {Array} stationsCoords Array of station coordinates.
   * @param {<LineString|MultiLineString} lineGeometry A LineString or a MultiLineString.
   * @param {string} color The color of the line.
   * @private
   */
  drawFullTrajectory(stationsCoords, lineGeometry, color) {
    // Don't allow white lines, use red instead.
    const vehiculeColor = /#ffffff/i.test(color) ? '#ff0000' : color;
    const vectorSource = this.vectorLayer.getSource();
    vectorSource.clear();

    if (stationsCoords) {
      const geometry = new MultiPoint(stationsCoords);
      const aboveStationsFeature = new Feature(geometry);
      aboveStationsFeature.setStyle(
        new Style({
          zIndex: 1,
          image: new Circle({
            radius: 5,
            fill: new Fill({
              color: '#000000',
            }),
          }),
        }),
      );
      const belowStationsFeature = new Feature(geometry);
      belowStationsFeature.setStyle(
        new Style({
          zIndex: 4,
          image: new Circle({
            radius: 4,
            fill: new Fill({
              color: this.useDelayStyle ? '#a0a0a0' : vehiculeColor,
            }),
          }),
        }),
      );
      vectorSource.addFeatures([aboveStationsFeature, belowStationsFeature]);
    }

    const lineFeat = new Feature({
      geometry: lineGeometry,
    });
    lineFeat.setStyle([
      new Style({
        zIndex: 2,
        stroke: new Stroke({
          color: '#000000',
          width: 6,
        }),
      }),
      new Style({
        zIndex: 3,
        stroke: new Stroke({
          color: this.useDelayStyle ? '#a0a0a0' : vehiculeColor,
          width: 4,
        }),
      }),
    ]);
    vectorSource.addFeature(lineFeat);
  }

  /**
   * Fetch stations information with a trajectory ID
   * @param {number} trajId The ID of the trajectory
   * @private
   */
  updateTrajectoryStations(trajId) {
    return super.updateTrajectoryStations(trajId).then((trajStations) => {
      this.stationsCoords = [];
      trajStations.stations.forEach((station) => {
        this.stationsCoords.push(
          transformCoords(station.coordinates, 'EPSG:4326', 'EPSG:3857'),
        );
      });

      this.highlightTrajectory();
      return trajStations;
    });
  }

  highlightTrajectory() {
    this.api
      .fetchTrajectoryById(
        this.getParams({
          id: this.journeyId,
          time: getUTCTimeString(new Date()),
        }),
      )
      .then((traj) => {
        const { p: multiLine, t, c } = traj;
        const lineCoords = [];
        multiLine.forEach((line) => {
          line.forEach((point) => {
            lineCoords.push([point.x, point.y]);
          });
        });

        this.drawFullTrajectory(
          this.stationsCoords,
          new LineString(lineCoords),
          c ? `#${c}` : getBgColor(t),
        );
      })
      .catch(() => {
        this.vectorLayer.getSource().clear();
      });
  }

  /**
   * @override
   */
  getParams(extraParams = {}) {
    const ext = this.map.getView().calculateExtent();
    const bbox = buffer(ext, getWidth(ext) / 10).join(',');
    const zoom = this.map.getView().getZoom();

    return super.getParams({
      ...extraParams,
      bbox,
      s: zoom < 10 ? 1 : 0,
      z: zoom,
    });
  }

  defaultStyle(props) {
    const zoom = this.map.getView().getZoom();
    return super.defaultStyle(props, zoom);
  }
}

export default TrajservLayer;

